CREATE TABLE IF NOT EXISTS `user_config` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user` int(11) NOT NULL,
  `item` varchar(255) NOT NULL,
  `value` text,
  PRIMARY KEY (`id`),
  UNIQUE KEY `user-item` (`user`,`item`),
  KEY `item` (`item`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;

CREATE TABLE IF NOT EXISTS `user_favorite_project` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user` int(11) NOT NULL,
  `project` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `user` (`user`),
  KEY `project` (`project`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

CREATE TABLE IF NOT EXISTS `user_follow` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `idol` int(11) NOT NULL,
  `fan` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `idol` (`idol`),
  KEY `fan` (`fan`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

CREATE TABLE IF NOT EXISTS `user_profile` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user` int(11) NOT NULL,
  `name` varchar(255) NOT NULL,
  `content` varchar(255) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `user` (`user`),
  KEY `name` (`name`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=3 ;

CREATE TABLE IF NOT EXISTS `user_status` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user` int(11) NOT NULL,
  `type` varchar(255) DEFAULT NULL,
  `content` varchar(255) NOT NULL,
  `url` varchar(255) DEFAULT NULL,
  `time` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `user` (`user`),
  KEY `time` (`time`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;

CREATE TABLE IF NOT EXISTS `user_status_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `status` int(11) NOT NULL,
  `content` varchar(255) NOT NULL,
  `user` int(11) NOT NULL,
  `time` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `status` (`status`),
  KEY `user` (`user`),
  KEY `time` (`time`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

CREATE TABLE IF NOT EXISTS `version` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `num` int(11) NOT NULL,
  `project` int(11) NOT NULL,
  `wit` int(11) NOT NULL,
  `name` varchar(255) NOT NULL,
  `content` text NOT NULL,
  `score_witower` decimal(3,1) NOT NULL,
  `score_company` decimal(3,1) NOT NULL,
  `comment_witower` varchar(255) NOT NULL DEFAULT '',
  `comment_company` varchar(255) NOT NULL DEFAULT '',
  `deleted` tinyint(1) NOT NULL,
  `user` int(11) NOT NULL,
  `time` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `user` (`user`),
  KEY `time` (`time`),
  KEY `wit` (`wit`),
  KEY `project` (`project`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=7 ;

INSERT INTO `version` (`id`, `num`, `project`, `wit`, `name`, `content`, `score_witower`, `score_company`, `comment_witower`, `comment_company`, `deleted`, `user`, `time`) VALUES
(1, 1, 1, 1, '智塔网网络服务使用协议', '<p style="margin: 0in; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 16pt;"><span lang="zh-CN" style="font-family: 宋体; font-size: 9pt; font-weight: bold;">《</span><span lang="en-US" style="font-family: 宋体; font-size: 9pt; font-weight: bold;">&nbsp;</span><span lang="zh-CN" style="font-family: 宋体; font-size: 9pt; font-weight: bold;">智塔网络服务使用协议</span><span lang="en-US" style="font-family: 宋体; font-size: 9pt; font-weight: bold;">&nbsp;</span><span lang="zh-CN" style="font-family: 宋体; font-size: 9pt; font-weight: bold;">》</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">智塔网（以下简称"本网站"）依据《智塔网服务协议》（以下简称"本协议"）的规定提供服务，本协议具有合同效力。注册会员时，请您认真阅读本协议，审阅并接受或不接受本协议（未成年人应在法定监护人陪同下审阅）。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">若您已经注册为本网站会员，即表示您已充分阅读、理解并同意自己与本网站订立本协议，且您自愿受本协议的条款约束。本网站有权随时变更本协议并在本网站上予以公告。经修订的条款一经在本网站的公布后，立即自动生效。如您不同意相关变更，必须停止使用本网站。本协议内容包括协议正文及所有智塔网已经发布的各类规则。所有规则为本协议不可分割的一部分，与本协议正文具有同等法律效力。一旦您继续使用本网站，则表示您已接受并自愿遵守经修订后的条款。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt;"><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">第一条</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">会员资格&nbsp;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">1、只有符合下列条件之一的自然人或法人才能申请成为本网站会员，可以使用本网站的服务；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、年满十八岁，并具有民事权利能力和民事行为能力的自然人；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、无民事行为能力人或限制民事行为能力人应经过其监护人的同意；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">C、根据中国法律、法规、行政规章成立并合法存在的机关、企事业单位、社团组织和其他组织。无法人资格的单位或组织不当注册为本网站会员的，其与本网站之间的协议自始无效，本网站一经发现，有权立即终止对该会员的服务，并追究其使用本网站服务的一切法律责任。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">2、会员需要根据网站提示，提供明确的联系地址和联系电话，并提供真实姓名或名称以及其他必要的信息。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt;"><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">第二条</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">会员的权利和义务</span><span lang="zh-CN" style="font-family: 宋体;">&nbsp;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">1</span><span lang="zh-CN">、所有会员须自行负责自己的会员账号和密码，且须对在会员账号密码下发生的所有活动（包括但不限于参与创意需求文案、网上点击同意各类协议、规则、参加创意项目投票、申请提取所获取的悬赏金额等行为）承担责任。会员有权根据需要更改登录和账户提现密码。因会员的过错导致的任何损失由会员自行承担，该过错包括但不限于：不按照交易提示操作，未及时进行交易操作，遗忘或泄漏密码，密码被他人破解，您使用的计算机被他人侵入等；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">2</span><span lang="zh-CN">、会员应当向本网站提供真实准确的注册信息，包括但不限于真实姓名、身份证号、正确的银行帐号（用于领取申请的悬赏）、联系电话、地址、邮政编码等。保证本网站可以通过上述联系方式与自己进行联系。同时，会员也应当在相关资料实际变更时及时更新有关注册资料；会员在进行悬赏兑现时应提供准确的</span><span lang="en-US">,</span><span lang="zh-CN">与之相匹配的银行信息</span><span lang="en-US">,</span><span lang="zh-CN">保证本网站能及时为用户兑现</span><span lang="en-US">;</span><span lang="zh-CN">若因为会员的疏忽或其他会员自身原因造成的会员损失</span><span lang="en-US">,</span><span lang="zh-CN">本网站不负担任何责任</span><span lang="en-US">;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">3</span><span lang="zh-CN">、会员不得以任何形式擅自转让或授权他人使用自己在本网站的会员帐号；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">4</span><span lang="zh-CN">、会员有义务确保在本网站上填写信息的真实、准确，无误导性；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">5</span><span lang="zh-CN">、会员在本网站网上平台，不得发布国家法律、法规、行政规章规定禁止的信息、侵犯他人知识产权或其他合法权益的信息、违背社会公共利益或公共道德的信息；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">6</span><span lang="zh-CN">、会员承诺自己在使用本网站实施的所有行为遵守法律、法规、行政规章和本网站的相关规定以及各种社会公共利益或公共道德。如有违反导致任何法律后果的发生，会员将以自己的名义独立承担相应的法律责任；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">7</span><span lang="zh-CN">、会员在本网站网上交易过程中如与其他会员因交易产生纠纷，可以请求本网站从中予以协调处理。会员如发现其他会员有违法或违反本协议的行为，可以向本网站举报；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">8</span><span lang="zh-CN">、会员应当自行承担因交易产生或取得的相关费用，并依法纳税；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">9</span><span lang="zh-CN">、会员不得使用以下方式登录网站或破坏网站所提供的服务：</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、以任何机器人软件、蜘蛛软件、爬虫软件、刷屏软件或其它自动方式访问或登录本网站；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、通过任何方式对本公司内部结构造成或可能造成不合理或不合比例的重大负荷的行为；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">C、通过任何方式干扰或试图干扰网站的正常工作或网站上进行的任何活动。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">10</span><span lang="zh-CN">、会员同意接收来自本网站的信息，包括但不限于活动信息、交易信息、促销信息等。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">会员应不时关注并遵守本站不时公布或修改的各类合法规则规定。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">本站保有删除站内各类不符合法律政策或不真实的信息内容而无须通知会员的权利。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">若会员未遵守以上规定的，本站有权作出独立判断并采取暂停或关闭会员帐号等措施。会员须对自己在网上的言论和行为承担法律责任。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #595959;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">第三条 本网站的权利和义务</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="zh-CN">1、目前本网站为会员提供一个信息交流平台，是企业会员发布自身品牌的广告创意需求和个人会员提供创意方案文案的一个交易市场，本网站对交易双方在法律规定的范围内进行协助和管理；本网站有义务对个人会员在本网站合法合理取得的悬赏积分进行兑换等值金额的货币；个人会员也可以使用获得的悬赏积分</span><span lang="en-US">,</span><span lang="zh-CN">购买本网站及其合作伙伴在智塔网进行出售的商品和服务</span><span lang="en-US">;</span><span lang="zh-CN">本网站有权对个人会员获得的悬赏积分进行追溯和核查</span><span lang="en-US">,</span><span lang="zh-CN">对不合理或者非法渠道获得的积分</span><span lang="en-US">,</span><span lang="zh-CN">本网站有权进行相关处理</span><span lang="en-US">,</span><span lang="zh-CN">并保留付诸法律的权利</span><span lang="en-US">;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">2、本网站有义务在现有技术水平的基础上努力确保整个网上交流平台的正常运行，尽力避免服务中断或将中断时间限制在最短时间内，保证会员在本网站上交流活动的顺利进行；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">3、本网站有义务对会员在注册和使用本网站信息平台中所遇到的与交易或注册有关的问题及反映的情况及时作出回复；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">4、本网站有权对会员的注册资料进行审查，对存在任何问题或怀疑的注册资料，本网站有权发出通知询问会员并要求会员做出解释、改正；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">5、会员因在本网站网上交易与其他会员产生纠纷的，会员将纠纷告知本网站，或本网站知悉纠纷情况的，经审核后，本网站有权通过电子邮件及电话联系向纠纷双方了解纠纷情况，并将所了解的情况通过电子邮件互相通知对方；会员通过司法机关依照法定程序要求本网站提供相关资料，本网站将积极配合并提供有关资料；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">6、因网上信息平台的特殊性，本网站没有义务对所有会员的交易行为以及与交易有关的其他事项进行事先审查，但如发生以下情形，本网站有权无需征得会员的同意限制会员的活动、向会员核实有关资料、发出警告通知、暂时中止、无限期中止及拒绝向该会员提供服务：</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、会员违反本协议或因被提及而纳入本协议的相关规则；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、存在会员或其他第三方通知本网站，认为某个会员或具体交易事项存在违法或不当行为，并提供相关证据，而本网站无法联系到该会员核证或验证该会员向本网站提供的任何资料；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">C、存在会员或其他第三方通知本网站，认为某个会员或具体交易事项存在违法或不当行为，并提供相关证据。本网站以普通非专业交易者的知识水平标准对相关内容进行判别，可以明显认为这些内容或行为可能对本网站会员或本网站造成财务损失或法律责任。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">7、根据国家法律、法规、行政规章规定、本协议的内容和本网站所掌握的事实依据，可以认定该会员存在违法或违反本协议行为以及在本网站平台上的其他不当行为，本网站有权无需征得会员的同意在本网站交易平台及所在网站上以网络发布形式公布该会员的违法行为，并有权随时作出删除相关信息、终止服务提供等处理；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="zh-CN">8、本网站依据本协议及相关规则，可以冻结并处置个人会员在本网站账户内的悬赏积分。被封号的个人会员如果对此存有异议</span><span lang="en-US">,</span><span lang="zh-CN">可以在对网站申请复议以便进一步处理；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="zh-CN">9、本网站有权在不通知会员的前提下，删除或采取其他限制性措施处理下列信息：包括但不限于反动</span><span lang="en-US">\\</span><span lang="zh-CN">色情</span><span lang="en-US">\\</span><span lang="zh-CN">存在欺诈等恶意或虚假内容；存在恶意删改他人信息或其他试图扰乱正常交易秩序因素；该信息违反公共利益或可能严重损害本网站和其他会员合法利益的。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">第四条 服务的中断和终止</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">1、若会员申请终止本网站服务，需经本网站审核同意，方可解除与本网站的协议关系，但本网站仍保留下列权利：</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、本网站有权在法律、法规、行政规章规定的时间内保留该会员的资料,包括但不限于以前的会员资料、交易记录等；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、若终止服务之前，该会员在本网站交易平台上存在违法行为或违反本协议的行为，本网站仍可行使本协议所规定的权利。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">2</span><span lang="zh-CN">、会员存在下列情况，本网站可以终止向该会员提供服务：</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、在会员违反本协议及相关规则规定时，本网站有权终止向该会员提供服务。本网站将在中断服务时通知会员。但该会员在被本网站终止提供服务后，再一次直接或间接或以他人名义注册为本网站会员的，本网站有权再次单方面终止为该会员提供服务；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、本网站发现会员注册资料中主要内容是虚假的，本网站有权随时终止为该会员提供服务；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">C、本协议终止或更新时，会员未确认新的协议的；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">D、其它本网站认为需终止服务的情况。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: red;"><span style="font-weight: bold;">第五条 本网站的责任范围</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">当会员接受该协议时，会员应当明确了解并同意∶</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">1、本网站不能随时预见到任何技术上的问题或其他困难。该等困难可能会导致数据损失或其他服务中断。本网站是在现有技术基础上提供的服务。本网站不保证以下事项∶</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">A、本网站将符合所有会员的要求；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">B、本网站不受干扰、能够及时提供、安全可靠或免于出错；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">C、本服务使用权的取得结果是正确或可靠的。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">2、会员经由本网站取得的建议和资讯，无论其形式或表现，绝不构成本协议未明示规定的任何保证；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US" style="font-weight: bold;">3</span><span lang="zh-CN" style="font-weight: bold;">、本网站只是为会员提供一个广告文案及相关的服务交易的平台，对于会员所发布的广告需求的相关产品的质量及其他涉及厂家的问题，本网站一律不负任何连带担保责任；&nbsp;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US" style="font-weight: bold;">4</span><span lang="zh-CN" style="font-weight: bold;">、本网站提供与其它互联网上的网站或资源的链接，会员可能会因此连接至其它运营商经营的网站，但不表示本网站与这些运营商有任何关系。其它运营商经营的网 站均由各经营者自行负责，不属于本网站控制及负责范围之内。对于存在或来源于此类网站或资源的任何内容、广告、物品或其它资料，本网站亦不予保证或负责。因使用或依赖任何此类网站或资源发布的或经由此类网站或资源获得的任何内容、物品或服务所产生的任何损害或损失，本网站不负任何直接或间接的责任；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">第六条 知识产权</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">1、本网站及本网站所使用的任何相关软件、程序、内容，包括但不限于作品、图片、档案、资料、网站构架、网站版面的安排、网页设计、经由本网站或广告商向会员呈现的广告或资讯，均由本网站或其它权利人依法享有相应的知识产权，包括但不限于著作权、商标权、专利权或其它专属权利等，受到相关法律的保护。未经本网站或权利人明示授权，会员保证不修改、出租、出借、出售、散布本网站及本网站所使用的上述任何资料和资源，或根据上述资料和资源制作成任何种类产品；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">2、本网站授予会员不可转移及非专属的使用权，使会员可以通过单机计算机使用本网站的目标代码（以下简称"软件"），但会员不得且不得允许任何第三方复制、修改、创作衍生作品、进行还原工程、反向组译，或以其它方式破译或试图破译源代码，或出售、转让"软件"或对"软件"进行再授权，或以其它方式移转"软件"之任何权利。会员同意不以任何方式修改"软件"，或使用修改后的"软件"；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="zh-CN">3、会员不得经由非本网站所提供的界面使用本网站</span><span lang="en-US">;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">4</span><span lang="zh-CN">、会员通过本网站提供的服务，参与在本网站公开区域的任何内容，视为用户同意将参与的广告创意作品的知识产权部分划归发布企业会员所有。就相应的企业会员对其参与的项目创意提供的悬赏奖励作为知识产权的支付费用：</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; A</span><span lang="zh-CN">、按照智塔网的规定，由企业会员、智塔网评分以及个人会员投票，来决定每个创意项目的奖金的分配比例；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; B</span><span lang="zh-CN">、个人会员在评分及投票中没有获得悬赏奖励的，即使参与创作也视为将其参与创作的知识产权转移给相应的企业用户；个人会员对获奖比例及名单等存在异议的，可以在网站内部的系统中进行投诉，由智塔网进行核实后确认，并给出最终解释和</span><span lang="en-US">/</span><span lang="zh-CN">或调整；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; C</span><span lang="zh-CN">、本网站拥有对此规定及相关比例等的解释权和修改权。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">5</span><span lang="zh-CN">、获得相应知识产权的企业会员有权通过如下方式行使该部分权利，但是，会员户声明保留该内容的著作权或该内容从其他网站转载而附带有原网站的著作权声明者除外：</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; A</span><span lang="zh-CN">、通过复制、发行、信息网络传播、改编、翻译、汇编、摄制、出租、展览、表演、放映、广播等方式使用该内容；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; B</span><span lang="zh-CN">、将该内容用于开发和运营各类电信增值业务及无线互联网业务（如短信、彩信、WAP、IVR、手机报、手机客户端等形式），对其中精彩的图文进行推广，推广方式包括智塔网站首页、子频道等链接推广或编辑出版等等；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; C</span><span lang="zh-CN">、推广市场授权他人使用该内容，授权第三方标示来源于智塔网站；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; D</span><span lang="zh-CN">、对任何三方侵犯该内容的知识产权的行为独立起诉进行维权。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">6</span><span lang="zh-CN">、个人用户参与部分的内容，若侵害到第三方的知识产权及相关合法权益，智塔网和</span><span lang="en-US">/</span><span lang="zh-CN">或企业用户不承担责任，并对因此侵权行为带来的损害，保留采取法律行动的权利。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">7</span><span lang="zh-CN">、若您认为本网站刊载的内容侵犯您的知识产权（包括著作权、商标专用权等）、名誉权、肖像权等合法权益，请您与本网站联系，并向提交书面通知，要求删除涉嫌侵权内容。该通知书应当包括下列内容：</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; A</span><span lang="zh-CN">、权利人的姓名（名称）、联系电话和通讯地址；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; B</span><span lang="zh-CN">、要求删除的涉嫌侵权内容的具体名称及其对应的网络地址；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; C</span><span lang="zh-CN">、构成侵权的初步证明材料，包括权利人身份证明或企业营业执照、权利权属证明等。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">第七条 隐私权</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">1、信息使用：</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、本网站不会向任何人出售或出借会员的个人或法人信息，除非事先得到会员得许可；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、本网站亦不允许任何第三方以任何手段收集、编辑、出售或者无偿传播会员的个人或法人信息。任何会员如从事上述活动，一经发现，本网站有权立即终止与该会员的服务协议，查封其账号。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">2、信息披露：会员的个人或法人信息将在下述情况下部分或全部被披露：&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、经会员同意，向第三方披露；&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、若会员是合法的知识产权使用权人并提起投诉，应被投诉人要求，向被投诉人披露，以便双方处理可能的权利纠纷；&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="zh-CN">C、根据法律的有关规定，或者行政、司法机关的要求，向第三方或者行政、司法机关披露</span><span lang="en-US">;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">D、若会员出现违反中国有关法律或者网站规定的情况，需要向第三方披露；&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">E、为提供你所要求的产品和服务，而必须和第三方分享会员的个人或法人信息；&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">F、其它本网站根据法律或者网站规定认为合适的披露。&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">3、信息安全：&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、在使用本网站服务进行网上交易时，请会员妥善保护自己的个人或法人信息，仅在必要的情形下向他人提供；&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、如果会员发现自己的个人或法人信息泄密，尤其是会员账户或"支付账户管理"账户及密码发生泄露，请会员立即联络本网站客服，以便我们采取相应措施。&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">第八条 不可抗力</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">因不可抗力或者其他意外事件，使得本协议的履行不可能、不必要或者无意义的，双方均不承担责任。本合同所称之不可抗力意指不能预见、不能避免并不能克服的客观情况，包括但不限于战争、台风、水灾、火灾、雷击或地震、罢工、暴动、法定疾病、黑客攻击、网络病毒、电信部门技术管制、政府行为或任何其它自然或人为造成的灾难等客观情况。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">第九条 保密</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">双方保证在对讨论、签订、执行本协议中所获悉的属于对方的且无法自公开渠道获得的文件及资料（包括但不限于商业秘密、公司计划、运营活动、财务信息、技术信息、经营信息及其他商业秘密）予以保密。未经该资料和文件的原提供方同意，另一方不得向第三方泄露该商业秘密的全部或者部分内容。但法律、法规、行政规章另有规定或者双方另有约定的除外。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: red;"><span style="font-weight: bold;">第十条 争议解决方式</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">1、本协议及其规则的有效性、履行和与本协议及其规则效力有关的所有事宜，将受中华人民共和国法律管辖，任何争议仅适用中华人民共和国法律；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">2、</span><span style="font-weight: bold;">如因智塔网违反有关法律、法规或本协议项下的任何条款而给会员造成损失，智塔网同意承担由此造成的损害赔偿责任。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">3、会员</span><span style="font-weight: bold;">同意保障和维护智塔网及其他会员的利益，如因会员违反有关法律、法规或本协议项下的任何条款而给智塔网或任何其他第三人造成损失，会员同意承担由此造成的损害赔偿责任。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US" style="font-weight: bold;">4</span><span lang="zh-CN" style="font-weight: bold;">、</span><span lang="zh-CN" style="font-weight: bold;">如双方就本协议内容或其执行发生任何争议，双方应尽量友好协商解决；协商不成时，任何一方均可向智塔网注册所在地的人民法院提起诉讼。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="zh-CN" style="font-weight: bold;">第十一条</span><span lang="zh-CN" style="font-weight: bold;">其他规定</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">1、本协议构成双方对本协议之约定事项及其他有关事宜的完整协议，除本协议规定的之外，未赋予本协议各方其他权利。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">2、如本协议中的任何条款无论因何种原因完全或部分无效或不具有执行力，本协议的其余条款仍应有效并且有约束力。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">3、本协议中的标题仅为方便而设，在解释本协议时应被忽略。&nbsp;</p>', 10.0, 0.0, '', '', 0, 4, 1388970667);
INSERT INTO `version` (`id`, `num`, `project`, `wit`, `name`, `content`, `score_witower`, `score_company`, `comment_witower`, `comment_company`, `deleted`, `user`, `time`) VALUES
(2, 1, 2, 2, '智塔网企业用户网络服务使用协议', '<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">欢迎您使用智塔企业平台服务！</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">特别提示：</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">在使用智塔企业平台服务之前，您应当认真阅读并遵守《智塔企业会员服务协议》（以下简称&ldquo;本协议&rdquo;），请您务必审慎阅读、充分理解各条款内容，特别是免除或者限制责任的条款、争议解决和法律适用条款。免除或者限制责任的条款可能将以加粗字体显示，您应重点阅读。如您对协议有任何疑问的，应向智塔网咨询。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">当您按照注册页面提示填写信息、阅读并同意本协议且完成全部注册程序后，或您按照激活页面提示填写信息、阅读并同意本协议且完成全部激活程序后，或您以其他智塔允许的方式实际使用智塔企业平台服务时，即表示您已充分阅读、理解并接受本协议的全部内容，并与智塔企业平台达成协议。您承诺接受并遵守本协议的约定， 届时您不应以未阅读本协议的内容或者未获得智塔网对您问询的解答等理由，主张本协议无效，或要求撤销本协议。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">一、 协议范围</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">1、本协议由您与智塔企业平台的经营者共同缔结，本协议具有合同效力。</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="zh-CN" style="font-family: 宋体;">智塔企业平台的经营者是指法律认可的经营智塔企业平台网站的责任主体，智塔企业平台网站包括但不限于智塔网（域名为</span><span lang="en-US" style="font-family: Calibri;">witower</span><span lang="zh-CN" style="font-family: 宋体;">.</span><span lang="en-US" style="font-family: Calibri;">com</span><span lang="zh-CN" style="font-family: 宋体;">）项下的企业服务。有关智塔企业平台经营者的信息请查看智塔网公开公布的公司信息和证照信息。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、除另行明确声明外，智塔企业平台服务包含任何智塔网及其关联公司提供的基于互联网以及移动网的相关服务，且均受本协议约束。如果您不同意本协议的约定，您应立即停止注册/激活程序或停止使用智塔企业平台服务。</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="zh-CN" style="font-family: 宋体;">3、本协议内容包括协议正文、线下协议和</span><span lang="en-US" style="font-family: Calibri;">/</span><span lang="zh-CN" style="font-family: 宋体;">或合同及所有智塔网已经发布或将来可能发布的各类规则、公告或通知（以下合称&ldquo;智塔规则&rdquo;或&ldquo;规则&rdquo;）。所有规则为本协议不可分割的组成部分，与协议正文具有同等法律效力。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">4、智塔网有权根据需要不时地制订、修改本协议及/或各类规则，并以网站公示的方式进行变更公告，无需另行单独通知您。变更后的协议和规则一经在网站公布后，立即或在公告明确的特定时间自动生效。若您在前述变更公告后继续使用智塔企业平台服务的，即表示您已经阅读、理解并接受经修订的协议和规则。若您不同意相关变更，应当立即停止使用智塔企业平台服务。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">二、 注册与账户</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">1、主体资格</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">您确认，在您完成注册程序或以其他智塔允许的方式实际使用智塔企业平台服务时，<span style="font-weight: bold;">您应当是具备完全民事行为能权利能力和完全民事力的自然人、法人或其他组织。若您不具备前述主体资格，则您及您的监护人应承担因此而导致的一切后果，且智塔网有权注销或永久冻结您的账户，并向您及您的监护人索偿相应损失。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、注册和账户</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">当您按照注册页面提示填写信息、阅读并同意本协议且完成全部注册程序后，或在您按照激活页面提示填写信息、阅读并同意本协议且完成全部激活程序后，或您以其他智塔网允许的方式实际使用智塔企业平台服务时，您即受本协议约束。您可以使用您提供或确认的邮箱、用户名或者智塔网允许的其它方式作为登录手段进入智塔企业平台。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">您了解并同意，如您在智塔企业平台注册成功，您即可以同步获得智塔网登录名。您可以通过您的智塔网登录名直接享有智塔网以及后续可能开通的其他网站/服务（以下合称&ldquo;智塔网&rdquo;），而无需重新注册。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c)</span><span lang="zh-CN" style="font-family: 宋体;">您可以对账户设置智塔用户名，您也可以通过该智塔登录用户名密码登录智塔企业平台。您设置的用户名不得侵犯或涉嫌侵犯他人合法权益。如您设置的用户名涉嫌侵犯他人合法权益，智塔网有权终止向您提供智塔企业平台服务，注销您的相关用户名，及至相关账号。用户名被注销后将开放给任意用户注册。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">d)</span><span lang="zh-CN" style="font-family: 宋体;">您的登录用户名、注册邮箱和密码不得以任何方式买卖、转让、赠与或继承，除非有法律明确规定或司法裁定，并经智塔网同意，且需提供智塔网要求的合格的文件材料并根据智塔网制定的操作流程办理。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">3、用户信息</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">在注册或激活流程时，您应当依照法律法规要求，按相应页面的提示准确提供您的资料，并于资料信息变更时及时更新，以保证您所提交资料的真实、及时、完整和准确。</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">如有合理理由怀疑您提供的资料错误、不实、过时或不完整的，智塔网有权向您发出询问及/或要求改正的通知，并有权直接做出删除相应资料的处理，直至中止、终止对您提供部分或全部智塔企业平台服务。智塔网对此不承担任何责任，您将承担因此产生的任何直接或间接损失及不利后果。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">您应当准确填写并及时更新您提供的电子邮件地址、联系电话、联系地址、邮政编码等联系方式，以便智塔网或其他用户与您进行有效联系，因通过这些联系方式无法与您取得联系，导致您在使用智塔企业平台服务过程中产生任何损失或增加费用的，应由您完全独自承担。您了解并同意，您有义务保持你提供的联系方式的真实性和有效性，如有变更或需要更新的，您应按智塔网的要求进行相应操作。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">4、账户安全</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="zh-CN" style="font-family: 宋体;">您须自行负责对您的智塔网登录名、注册邮件和密码保密，且须对您在该登录名、注册邮件和密码下发生的所有活动（包括但不限于信息披露、发布创意需求项目、参与相关项目及其评分、网上点击同意或提交各类规则协议、网上续签协议或购买服务等）承担责任。您同意：(</span><span lang="en-US" style="font-family: Calibri;">a</span><span lang="zh-CN" style="font-family: 宋体;">) 如发现任何人未经授权使用您的智塔登录名、注册邮箱和密码，或发生违反保密规定的任何其他情况，您会立即通知智塔网，并授权智塔网进行相关处理；及 (</span><span lang="en-US" style="font-family: Calibri;">b</span><span lang="zh-CN" style="font-family: 宋体;">) 确保您在每个上网时段结束时，以正确步骤离开网站/服务。智塔网不能也不会对因您未能遵守本款规定而发生的任何损失负责。您理解智塔网对您的请求采取行动需要合理时间，智塔网对在采取行动前已经产生的后果（包括但不限于您的任何损失）不承担任何责任。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">5、智塔网登录名注销</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">如您连续三年未使用您的邮箱、用户名或智塔网认可的其他方式和密码登录过智塔企业平台，您的智塔网企业用户可能被注销，不能再登录智塔网及相关网站，所有智塔网站服务同时终止。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">您同意并授权智塔网，您如在智塔网及相关站有欺诈、发布的产品涉及假冒伪劣/侵权商品、侵犯他人合法权益或其他违反法律法规、智塔网站规则等行为，您的智塔网企业用户可能被注销、不能再登录智塔网及相关网站，所有智塔网站服务同时终止。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">三、 智塔企业平台服务</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">1、通过智塔网提供的智塔企业平台服务和其它服务，作为企业会员，您可在智塔企业平台上：</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">创建和维护产品信息</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">发布和维护创意项目需求信息</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c)</span><span lang="zh-CN" style="font-family: 宋体;">设定各个发布项目相应的悬赏额度</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">d)</span><span lang="zh-CN" style="font-family: 宋体;">对自身发布的创意项目进行日常管理（包括但不限于对版本进行日常评分和评价、对相应版本进行删除或修改等）</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">e)</span><span lang="zh-CN" style="font-family: 宋体;">在自身项目进入到打分期时对其项目进行评分并选定创意需求最终方案</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">具体以所开通的智塔企业平台提供的服务内容为准。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、<span style="font-weight: bold;">您在智塔企业平台上交易过程中与其他会员发生交易纠纷时，一旦您或其它会员任一方或双方共同提交智塔网要求调处，则智塔网作为独立第三方，有权根据单方判断做出调处决定，您了解并同意接受智塔的判断和调处决定。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">3、<span style="font-weight: bold;">您了解并同意，智塔网有权应政府部门（包括司法及行政部门）的要求，向其提供您在智塔网提供的用户信息和交易记录等必要信息。如您涉嫌侵犯他人知识产权等合法权益，则智塔网亦有权在初步判断涉嫌侵权行为存在的情况下，向权利人提供您必要的身份信息。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">4、您在使用智塔企业平台服务过程中，所产生的应纳税赋，以及一切硬件、软件、服务及其它方面的费用，均由您独自承担。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">四、智塔企业平台服务使用规范</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">1、在智塔企业平台上使用相关服务过程中，您承诺遵守以下约定：</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">a</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)实施的所有行为均遵守国家法律、法规等规范性文件及智塔企业平台各项规则的规定和要求，不违背社会公共利益或公共道德，不损害他人的合法权益，不偷逃应缴税费，不违反本协议及相关规则。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b</span><span lang="zh-CN" style="font-family: 宋体;">)在智塔网的创意悬赏交易过程中，遵守诚实信用原则，不采取不正当竞争行为，不扰乱网上交易的正常秩序，不从事与网上交易无关的行为。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c</span><span lang="zh-CN" style="font-family: 宋体;">)不发布国家禁止销售的或限制销售的商品或服务信息（除非取得合法且足够的许可），不发布涉嫌侵犯他人知识产权或其它合法权益的商品或服务信息，不发布违背社会公共利益或公共道德或智塔认为不适合在智塔企业平台上进行的商品或服务信息，不发布其它涉嫌违法或违反本协议及各类规则的信息。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">d</span><span lang="zh-CN" style="font-family: 宋体;">)不以虚构或歪曲事实的方式不当评价其他参与会员，不采取不正当方式制造或提高自身的知名度。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">e)&nbsp;</span><span lang="zh-CN" style="font-family: 宋体;">企业会员对自身发布的项目所属产品或项目及商标等拥有合法的品牌使用权及网络使用权，不得发布自身不拥有所有权或使用权的品牌或商品相关的项目。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">f</span><span lang="en-US" style="font-family: 宋体;">)</span><span lang="zh-CN" style="font-family: 宋体;">企业会员对自身发布的项目的最终方案拥有所有权，同时对选中的创意方案的所有参与版本都拥有其知识产权。但对没有选中的方案及其所有的任意版本都不享有相应的所有权。注：知识产权的转移在该项目的悬赏结束，相关积分计入参与的网络个人用户时发生。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">g</span><span lang="zh-CN" style="font-family: 宋体;">)不使用任何装置、软件或例行程序干预或试图干预智塔企业平台的正常运作或正在智塔企业平台上进行的任何交易、活动。您不得采取任何将导致不合理的庞大数据负载加诸智塔企业平台网络设备的行动。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、您了解并同意：</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">a</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)您如果违反前述承诺，产生任何法律后果的，您应以自己的名义独立承担所有的法律责任，并确保智塔网免于因此产生任何损失或增加费用。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">b</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)基于维护智塔企业平台正常秩序及安全的需要，智塔网有权在发生恶意扰乱正常秩序的情形下，执行关闭创意项目等操作。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">c</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)经国家行政或司法机关的生效法律文书确认您的相关项目或其他信息存在违法或侵权行为，或者智塔网根据自身的判断，认为您的行为涉嫌违反法律法规的规定或涉嫌违反本协议和/或规则的条款的，则智塔网有权在智塔企业平台上公示您的该等涉嫌违法或违约行为及智塔网已对您采取的措施。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">d</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)对于您在智塔企业平台上发布的涉嫌违法、涉嫌侵犯他人合法权利或违反本协议和/或规则的信息，智塔网有权不经通知您即予以删除或修改等，且按照规则的规定进行处罚。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">e</span><span lang="zh-CN" style="font-family: 宋体;">)如您涉嫌违反有关法律或者本协议之规定，使智塔网遭受任何损失，或受到任何第三方的索赔，或受到任何行政管理部门的处罚，您应当赔偿智塔网因此造成的损失和/或发生的费用，包括合理的律师费用。同时智塔网保留进一步追究相应的法律责任及追索赔偿的权利。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">五、责任范围和责任限制</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">1、智塔网负责按"现状"和"可得到"的状态向您提供智塔企业平台服务。但智塔网对智塔企业平台服务不作任何明示或暗示的保证，包括但不限于智塔企业平台服务的适用性、没有错误或疏漏、持续性、准确性、可靠性、适用于某一特定用途。同时，智塔网也不对智塔企业平台服务所涉及的技术及信息的有效性、准确性、正确性、可靠性、质量、稳定、完整和及时性作出任何承诺和保证。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">2、您了解智塔企业平台上的信息系用户自行发布，且可能存在风险和瑕疵。目前智塔网为会员提供一个信息交流平台，包括但不限于企业会员发布自身品牌的广告创意需求和个人会员提供创意方案文案的一个交易市场，本网站对交易双方在法律规定的范围内进行协助和管理。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold;">3</span><span lang="zh-CN" style="font-weight: bold;">、智塔网有权为参与任意项目的个人用户的创意版本进行评分，同时评分按照智塔网规定的计算方法，以一定的权重比例用于计算最终的悬赏分配；同时智塔网也有权利和义务对参与企业的项目的版本进行日常的管理和维护，对参与的版本进行必要的删除、修改等处理。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">4</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">、除非法律法规明确要求，或出现以下情况，否则，智塔网没有义务对所有用户的信息数据、商品和服务信息、悬赏交易行为以及与悬赏交易有关的其它事项进行事先审查。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">5</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">、您了解并同意，智塔网不对因下述任一情况而导致您的任何损害赔偿承担责任，包括但不限于利润、商誉、使用、数据等方面的损失或其它无形损失的损害赔偿 ：</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">a)使用或未能使用智塔企业平台服务。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">b)第三方未经批准的使用您的账户或更改您的数据。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">c)您对智塔企业平台服务的误解。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold;">d</span><span lang="zh-CN" style="font-weight: bold;">)任何非因智塔网的原因而引起的与智塔企业平台服务有关的其它损失。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">6</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">、不论在何种情况下，智塔网均不对由于信息网络正常的设备维护，信息网络连接故障，电脑、通讯或其他系统的故障，电力故障，罢工，劳动争议，暴乱，起义，骚乱，生产力或生产资料不足，火灾，洪水，风暴，爆炸，战争，政府行为，司法行政机关的命令或第三方的不作为而造成的不能服务或延迟服务承担责任。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">六、知识产权</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">1、智塔网及智塔网所使用的任何相关软件、程序、内容，包括但不限于作品、图片、档案、资料、网站构架、网站版面的安排、网页设计、经由本网站或广告商向会员呈现的广告或资讯，均由智塔网或其它权利人依法享有相应的知识产权，包括但不限于著作权、商标权、专利权或其它专属权利等，受到相关法律的保护。未经本网站或权利人明示授权，会员保证不修改、出租、出借、出售、散布本网站及本网站所使用的上述任何资料和资源，或根据上述资料和资源制作成任何种类产品。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、智塔网授予会员不可转移及非专属的使用权，使会员可以通过单机计算机使用本网站的目标代码（以下简称"软件"），但会员不得且不得允许任何第三方复制、修改、创作衍生作品、进行还原工程、反向组译，或以其它方式破译或试图破译源代码，或出售、转让"软件"或对"软件"进行再授权，或以其它方式移转"软件"之任何权利。会员同意不以任何方式修改"软件"，或使用修改后的"软件"。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">3、会员不得经由非智塔网所提供的界面使用本网站。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span lang="en-US">4</span><span lang="zh-CN">、会员通过智塔网提供的服务，参与在智塔网公开区域的任何内容，视为参与创作用户同意将参与的广告创意作品的知识产权部分划归发布企业会员所有。就相应的企业会员对其参与的项目创意提供的悬赏奖励作为知识产权的支付费用：</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">按照智塔网的规定，由企业会员、智塔网评分以及个人会员投票，来决定每个创意项目的奖金的分配比例；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">个人会员在评分及投票中没有获得悬赏奖励的，即使参与创作也视为将其参与创作的知识产权转移给相应的企业用户；个人会员</span><span lang="en-US" style="font-family: Calibri;">/</span><span lang="zh-CN" style="font-family: 宋体;">企业用户对获奖比例及名单等存在异议的，可以在网站内部的系统中进行投诉，由智塔网进行核实后确认，并给出最终解释和</span><span lang="en-US" style="font-family: 宋体;">/</span><span lang="zh-CN" style="font-family: 宋体;">或调整；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c)</span><span lang="zh-CN" style="font-family: 宋体;">智塔网拥有对此规定及相关比例等的解释权和修改权。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span lang="en-US">5</span><span lang="zh-CN">、获得相应知识产权的企业会员有权通过如下方式行使该部分权利，但是，会员声明保留该内容的著作权或该内容从其他网站转载而附带有原网站的著作权声明者除外：</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">通过复制、发行、信息网络传播、改编、翻译、汇编、摄制、出租、展览、表演、放映、广播等方式使用该内容；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">将该内容用于开发和运营各类电信增值业务及无线互联网业务（如短信、彩信、</span><span lang="en-US" style="font-family: 宋体;">WAP</span><span lang="zh-CN" style="font-family: 宋体;">、</span><span lang="en-US" style="font-family: 宋体;">IVR</span><span lang="zh-CN" style="font-family: 宋体;">、手机报、手机客户端等形式），对其中精彩的图文进行推广，推广方式包括智塔网站首页、子频道等链接推广或编辑出版等等；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c)</span><span lang="zh-CN" style="font-family: 宋体;">推广市场授权他人使用该内容，授权第三方标示来源于智塔网站；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">d)</span><span lang="zh-CN" style="font-family: 宋体;">对任何三方侵犯该内容的知识产权的行为独立起诉进行维权。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span lang="en-US">6</span><span lang="zh-CN">、个人用户参与部分的内容，若侵害到第三方的知识产权及相关合法权益，智塔网和</span><span lang="en-US">/</span><span lang="zh-CN">或企业用户不承担责任，并对因此侵权行为带来的损害，保留采取法律行动的权利。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span lang="en-US">7</span><span lang="zh-CN">、若您认为本网站刊载的内容侵犯您的知识产权（包括著作权、商标专用权等）、名誉权、肖像权等合法权益，请您与本网站联系，并向提交书面通知，要求删除涉嫌侵权内容。该通知书应当包括下列内容：</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">权利人的姓名（名称）、联系电话和通讯地址；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">要求删除的涉嫌侵权内容的具体名称及其对应的网络地址；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c)</span><span lang="zh-CN" style="font-family: 宋体;">构成侵权的初步证明材料，包括权利人身份证明或企业营业执照、权利权属证明等。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">七、协议终止</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">1、出现以下情况时，智塔网有权直接以注销账户的方式终止本协议，并有权永久冻结（注销）您的账户在智塔企业平台的权限和收回账户对应的用户名：</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">a</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)智塔网终止向您提供服务后，您涉嫌再一次直接或间接以他人名义注册为智塔用户的；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">b</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)您提供的电子邮箱不存在或无法接收电子邮件，且没有其他方式可以与您进行联系，或智塔网以其它联系方式通知您更改电子邮件信息，而您在智塔网通知后三个工作日内仍未更改为有效的电子邮箱的。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">c</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)您提供的用户信息中的主要内容不真实或不准确或不及时或不完整；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">d</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)本协议（含规则）变更时，您明示并通知智塔网不愿接受新的服务协议的；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">e</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)其它智塔网认为应当终止服务的情况。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">2</span><span lang="zh-CN" style="font-family: 宋体;">、</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">您的账户服务被终止或者账户在智塔企业平台的权限被永久冻结（注销）后，</span><span lang="zh-CN" style="font-family: 宋体;">智塔网没有义务为您保留或向您披露您账户中的任何信息，也没有义务向您或第三方转发任何您未曾阅读或发送过的信息。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">3</span><span lang="zh-CN" style="font-family: 宋体;">、您同意，您与智塔网的合同关系终止后，智塔网仍享有下列权利：</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a</span><span lang="zh-CN" style="font-family: 宋体;">)继续保存您的用户信息及您使用智塔企业平台服务期间的所有交易信息。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b</span><span lang="zh-CN" style="font-family: 宋体;">)您在使用智塔企业平台服务期间存在违法行为或违反本协议和/或规则的行为的，智塔网仍可依据本协议向您主张权利。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">5、智塔网中止或终止向您提供智塔企业平台服务后，对于您在服务中止或终止之前的交易行为依下列原则处理，您应独力处理并完全承担进行以下处理所产生的任何争议、损失或增加的任何费用，并应确保智塔网免于因此产生任何损失或承担任何费用：</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a</span><span lang="zh-CN" style="font-family: 宋体;">)您在服务中止或终止之前已经上传至淘宝平台的产品和项目信息，或项目尚未进入悬赏交易的，智塔网有权在中止或终止服务的同时删除此项产品或项目的相关信息；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b</span><span lang="zh-CN" style="font-family: 宋体;">)您在服务中止或终止之前发布悬赏交易，但合同尚未实际履行完成的，智塔网有权删除该创意需求信息及其产品或项目的相关信息；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c</span><span lang="zh-CN" style="font-family: 宋体;">)您在服务中止或终止之前，发布的悬赏需求进入到实际履行阶段（包括但不限于有用户参与、进入到打分期或投票阶段等），相当于与其他会员达成悬赏合同且已部分履行的，智塔网可以不删除该项项目，但智塔网有权在中止或终止服务的同时将相关情形通知您的交易对方。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">八、隐私权政策</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">1、信息使用：</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">智塔网不会向任何人出售或出借会员的个人或法人信息，除非事先得到会员得许可；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">智塔网亦不允许任何第三方以任何手段收集、编辑、出售或者无偿传播会员的个人或法人信息。任何会员如从事上述活动，一经发现，智塔网有权立即终止与该会员的服务协议，查封其账号。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、信息披露：会员的个人或法人信息将在下述情况下部分或全部被披露：&nbsp;</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">经会员同意，向第三方披露；&nbsp;</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">若会员是合法的知识产权使用权人并提起投诉，应被投诉人要求，向被投诉人披露，以便双方处理可能的权利纠纷；&nbsp;</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c)</span><span lang="zh-CN" style="font-family: 宋体;">根据法律的有关规定，或者行政、司法机关的要求，向第三方或者行政、司法机关披露</span><span lang="en-US" style="font-family: Calibri;">;</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">d)</span><span lang="zh-CN" style="font-family: 宋体;">若会员出现违反中国有关法律或者网站规定的情况，需要向第三方披露；&nbsp;</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">e)</span><span lang="zh-CN" style="font-family: 宋体;">为提供你所要求的产品和服务，而必须和第三方分享会员的个人或法人信息；&nbsp;</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">f)</span><span lang="zh-CN" style="font-family: 宋体;">其它智塔网根据法律或者网站规定认为合适的披露。&nbsp;</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">3、信息安全：&nbsp;</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">在使用智塔网服务进行网上交易时，请会员妥善保护自己的个人或法人信息，仅在必要的情形下向他人提供；&nbsp;</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">如果会员发现自己的个人或法人信息泄密，尤其是会员账户账户及密码发生泄露，请会员立即联络智塔网客服，以便我们采取相应措施。&nbsp;</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">智塔网将在智塔网相关平台公布并不时修订隐私权政策，隐私权政策构成本协议的有效组成部分。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">九、法律适用、管辖与其他</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">1、本协议之效力、解释、变更、执行与争议解决<span style="font-weight: bold;">均适用中华人民共和国大陆地区法律</span>，如无相关法律规定的，则应参照通用国际商业惯例和/或行业惯例。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、本协议包含了您使用智塔企业平台需遵守的一般性规范，同时您还需遵守适用于该平台的特殊性规范（具体请参见您与该平台签署的其他协议以及平台规则等内容）。一般性规范如与特殊性规范不一致或有冲突，则特殊性规范具有优先效力。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">3、因本协议产生之争议需根据您使用的服务归属的平台确定具体的争议对象，例如因您使用智塔网服务所产生的争议应由智塔网的经营者与您沟通并处理。<span style="font-weight: bold;">一旦产生争议，您与智塔网平台的经营者均同意以被告住所地人民法院为第一审管辖法院。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">4、如本协议的中文版本和英文版本内容产生冲突，有其他语言版本，将以中文版本内容为准。本协议以其中文版本为最终生效版本。</p>\n<p>&nbsp;</p>', 0.0, 0.0, '', '', 0, 4, 1392019695);
INSERT INTO `version` (`id`, `num`, `project`, `wit`, `name`, `content`, `score_witower`, `score_company`, `comment_witower`, `comment_company`, `deleted`, `user`, `time`) VALUES
(3, 1, 3, 3, '智塔帮助 -- 项目常见问题', '<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">什么是创意？</span></strong></p>\n<p><span lang="EN-US" style="font-size: 10pt;">&nbsp;&nbsp; &nbsp;&nbsp;</span><span style="font-size: 10pt;">创意是网友为征集中的广告创意项目所设立的文案。一个项目可以有多个不同的文案，每个文案都独立作为一个创意。每个注册网友都可以随意参与到任何一个项目的文案的书写中。对于创意有以下几个限制：</span></p>\n<p style="text-indent: 20.1pt; mso-char-indent-count: 2.0;"><strong><span style="font-size: 10pt; font-family: 宋体;">每人只能最多发布一个新创意</span></strong></p>\n<p style="text-indent: 20.1pt; mso-char-indent-count: 2.0;"><strong><span style="font-size: 10pt; font-family: 宋体;">每个项目最多有五个创意</span></strong></p>\n<p style="text-indent: 20.1pt; mso-char-indent-count: 2.0;"><strong><span style="font-size: 10pt; font-family: 宋体;">每个创意是若干个版本共同组成的</span></strong></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">什么是版本？</span></strong></p>\n<p><span lang="EN-US" style="font-size: 10pt;">&nbsp;&nbsp; &nbsp;&nbsp;</span><span style="font-size: 10pt;">每个参与者的每次对创意的修改，都会保存成为一个版本。每个版本是追溯参与者贡献的重要因素。每个参与者的版本没有限制（我们鼓励大家参与，但是希望尽量减少版本的冗余数据）。</span></p>\n<p style="text-indent: 20.1pt; mso-char-indent-count: 2.0;"><strong><span style="font-size: 10pt; font-family: 宋体;">发布方和智塔会每天对进行中的项目版本进行审核和修订，并给出分数</span></strong></p>\n<p style="text-indent: 20.1pt; mso-char-indent-count: 2.0;"><strong><span style="font-size: 10pt; font-family: 宋体;">参与者的多个版本都可以被打分，在计算悬赏时分数会进行汇总至个人参与用户</span></strong></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">如何参加项目创意</span></strong></p>\n<p><span lang="EN-US" style="font-size: 10pt;">&nbsp; &nbsp; &nbsp;</span><span style="font-size: 10pt;">在智塔网参加项目创意很简单，只要您在项目页或首页找到想参与的品牌的广告需求项目，就可以使用编辑来用文字书写广告策划文案，来贡献自己的聪明才智了。</span></p>\n<p style="text-indent: 21.0pt;"><span style="font-size: 10pt;">目前还不能直接上传图片和视频（可以通过发布图片和视频的链接来呈现），我们会尽快完善这方面的功能，让智塔网的参与项目形式更加丰富多彩。</span></p>\n<p style="text-indent: 21.0pt;"><span style="font-size: 10pt;">由于大家可以共同为同一个创意贡献才智，希望参与者本着合作创新，求同存异的精神，为自己喜欢的品牌共同创造优秀的广告作品。</span></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">如何成为候选人</span></strong></p>\n<p class="MsoNormal" style="text-indent: 21.0pt;"><span style="font-size: 10pt; font-family: 宋体;">在您参与的项目中，发布方和智塔会尽量本着公正客观的立场为参与的版本进行打分，分数在<span lang="EN-US">1-10</span>分，分数越高表示对该版本越赞许。</span></p>\n<p class="MsoNormal" style="text-indent: 21.0pt;"><span style="font-size: 10pt; font-family: 宋体;">如果参与的这个创意在多个创意中被发布方选中，同时在这个方案中您发布的版本的分数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&gt;0</span><span style="font-size: 10pt; font-family: 宋体;">（即发布方或智塔给出的分数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&gt;0</span><span style="font-size: 10pt; font-family: 宋体;">），则您就会作为投票的候选人出现在投票列表中。</span></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">如何投票</span></strong></p>\n<p class="MsoNormal"><span lang="EN-US" style="font-size: 10pt; font-family: 宋体;">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 10pt; font-family: 宋体;">项目在打分期后，进入到三天的投票期。在投票期中，项目无法继续参与。同时在投票页会列示出候选人的名单。</span></p>\n<p class="MsoNormal"><span lang="EN-US" style="font-size: 10pt; font-family: 宋体;">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 10pt; font-family: 宋体;">每个人可以在每个项目的投票中，投上自己的（不多于）三票，来表达对候选人的支持。票数可以分别投给<span lang="EN-US">1-3</span>个候选人。</span></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">如何查看候选人贡献</span></strong></p>\n<p class="MsoNormal" style="text-indent: 20.0pt; mso-char-indent-count: 2.0;"><span style="font-size: 10pt; font-family: 宋体;">在投票页面的候选人列表中，每个候选人后面都有&ldquo;</span><span lang="EN-US" style="font-size: 12pt; font-family: Arial, sans-serif;">Ta</span><span style="font-size: 12pt; font-family: 华文行楷;">的贡献</span><span style="font-size: 10pt; font-family: 宋体;">&rdquo;链接，在这里你可以查询到该名候选人的在该中选的创意中的版本贡献，并可以和最终版本进行对比。</span></p>\n<p class="MsoNormal" style="text-indent: 21.0pt;"><span style="font-size: 10pt; font-family: 宋体;">由于创意的取舍及版本的打分，一定在某种程度上存在着主观的意识，我们很难完全做到让所有参与者都满意，我们会及时接受大家的反馈，虚心接受大家的建议和意见。</span><span style="font-size: 10pt; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;">同时也希望大家本着互谅合作的精神，在智塔这个创意平台中积极发挥自己的创意才智。</span></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">如何获得悬赏</span></strong></p>\n<p class="MsoNormal" style="text-indent: 21.0pt;"><span style="font-size: 10pt; font-family: 宋体;">在投票后，中选的候选人按照智塔网独特的、更加透明公正的悬赏分配比例计算方式计算积分，计入个人的积分帐户。</span></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">悬赏计算方式</span></strong></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;</span><span style="font-size: 10pt;">智塔网设计了全新的悬赏分配方式，尽量做到公平公正和透明，并让所有用户都可以亲身参与到悬赏的分配中来。</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 1</span><span style="font-size: 10pt;">）发布方：在创意进行阶段和打分期，为参与者的版本打分数（</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1-10</span><span style="font-size: 10pt;">），分数越高代表对参与者的创意越满意；</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2</span><span style="font-size: 10pt;">）发布方：在创意结束后的打分期中（结束后的</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1</span><span style="font-size: 10pt;">天，在打分期中，创意是关闭的，无法继续参与），在多个创意方案中选定一个方案；</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 3</span><span style="font-size: 10pt;">）智塔网：在创意进行阶段和打分期，为参与者的版本打分数（</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1-10</span><span style="font-size: 10pt;">）；</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 4</span><span style="font-size: 10pt;">）您参与的这个方案被发布方选中，同时在这个方案中您发布的版本的分数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&gt;0</span><span style="font-size: 10pt;">（即发布方或智塔给出的分数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&gt;0</span><span style="font-size: 10pt;">），则您就会作为投票的候选人出现在投票列表中，最后的奖金分配必然有您的一杯羹；</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 5</span><span style="font-size: 10pt;">）方案在打分期后，进入到</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">3</span><span style="font-size: 10pt;">天的投票期。在投票期中，所有的注册用户都可以为心仪的候选人投上自己的票数（不多于</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">3</span><span style="font-size: 10pt;">票），来表示自己对候选人的支持；</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 6</span><span style="font-size: 10pt;">）投票结束后，进行最后的悬赏分配，按照</span><span style="font-size: 10pt;">注册用户投票</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">/</span><span style="font-size: 10pt;">发布方打分</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">/</span><span style="font-size: 10pt;">智塔打分</span><span style="font-size: 10pt;">比例为</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;"> 2</span><span style="font-size: 10pt;">：</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">2</span><span style="font-size: 10pt;">：</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1</span><span style="font-size: 10pt;">来计算最后的悬赏积分。例如：候选人</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">A</span><span style="font-size: 10pt;">在总数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">100</span><span style="font-size: 10pt;">票的注册用户投票中获得了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">30</span><span style="font-size: 10pt;">票；在企业的中选创意打分中，获得了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">20</span><span style="font-size: 10pt;">分（企业在该创意中总共给出了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">50</span><span style="font-size: 10pt;">分至数名候选人）；在智塔的该中选创意的打分中，获得了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">10</span><span style="font-size: 10pt;">分（智塔在该创意中总共给出了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">50</span><span style="font-size: 10pt;">分至数名候选人）。而该创意悬赏方案的悬赏金额为</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">5000</span><span style="font-size: 10pt;">元，则</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">A</span><span style="font-size: 10pt;">获得的悬赏积分计算如下：</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;">&nbsp;</p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span style="font-size: 10pt;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; [(30/100)X2+(20/50)X2+(10/50)X1]/(2+2+1)X5000=1600元</span></p>\n<h4 style="margin: 0cm; margin-bottom: .0001pt;"><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">&nbsp;</span></strong></h4>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">如何申请悬赏</span></strong></p>\n<p class="MsoNormal" style="text-indent: 20.0pt; mso-char-indent-count: 2.0;"><span style="font-size: 10.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-ascii-theme-font: minor-latin; mso-fareast-font-family: 宋体; mso-fareast-theme-font: minor-fareast; mso-hansi-font-family: Calibri; mso-hansi-theme-font: minor-latin;">在投票结束后，悬赏积分计入到候选人的个人积分帐户中之后，您可以随时在&ldquo;积分&rdquo;页面查看目前您的积分额度，只要大于</span><span lang="EN-US" style="font-size: 10.0pt;">0</span><span style="font-size: 10.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-ascii-theme-font: minor-latin; mso-fareast-font-family: 宋体; mso-fareast-theme-font: minor-fareast; mso-hansi-font-family: Calibri; mso-hansi-theme-font: minor-latin;">，您就可以在页面的&ldquo;申请提现&rdquo;中，输入您要提取的金额并选择申请提现。</span></p>\n<p class="MsoNormal" style="text-indent: 20.0pt; mso-char-indent-count: 2.0;"><span style="font-size: 10.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-ascii-theme-font: minor-latin; mso-fareast-font-family: 宋体; mso-fareast-theme-font: minor-fareast; mso-hansi-font-family: Calibri; mso-hansi-theme-font: minor-latin;">智塔在收到您的提现申请后，会在</span><span lang="EN-US" style="font-size: 10.0pt;">1-3</span><span style="font-size: 10.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-ascii-theme-font: minor-latin; mso-fareast-font-family: 宋体; mso-fareast-theme-font: minor-fareast; mso-hansi-font-family: Calibri; mso-hansi-theme-font: minor-latin;">个工作日内将您申请的额度按照您个人帐户信息，汇至您指定的银行中。</span></p>\n<p><span style="font-size: 10.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-ascii-theme-font: minor-latin; mso-fareast-theme-font: minor-fareast; mso-hansi-font-family: Calibri; mso-hansi-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA;">我们也会尽快的开通其他的电子支付方式，如支付宝，快钱等，来简化和方便您的提现。</span></p>', 0.0, 0.0, '', '', 0, 4, 1389074579),
(4, 1, 4, 4, '关于智塔网', '<p>&nbsp;</p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">智塔网是做什么的</span></strong><strong><span style="font-size: 14pt; font-family: 华文行楷;">：</span></strong><span style="font-size: 10pt;">智塔网是一家探索全新品牌广告推广商业模式的网站，在此我们提供一个自由开放的威客悬赏平台，由网民自主合作创作品牌广告和/或推广文案，并根据创意贡献和注册网友投票比例，来确定获得企业发布方提供的悬赏额度。</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">(</span><span style="font-size: 10pt;">如何确定贡献</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">?</span><span style="font-size: 10pt;">请参看</span><span style="font-size: 10pt;">悬赏分配方式<span lang="EN-US">)</span></span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">智塔网和其他的威客网站有什么区别：</span></strong></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt;">&nbsp; &nbsp; &nbsp;1</span><span style="font-size: 10pt;">）在智塔网，我们先期主要提供品牌广告文案的悬赏参与。这意味着参与者无需多么专业的知识，也无需花巨大的精力在设计或者开发上，只要您有</span><span style="font-size: 12pt; font-family: 华文行楷; color: red;">参与创作的热情</span><span style="font-size: 12pt; font-family: 宋体;">，</span><span style="font-size: 12pt; font-family: 华文行楷; color: red;">善于观察生活的心</span><span style="font-size: 10pt; font-family: 宋体;">，</span><span style="font-size: 10pt;">再加上</span><span style="font-size: 10pt;"><span style="font-size: 10.5pt; font-family: 华文行楷; color: red;">一点奇思妙想</span>以及</span><span style="font-size: 10.5pt; font-family: 华文行楷; color: red;">乐于和他人分享合作的精神</span><span style="font-size: 10pt;">。</span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt;">&nbsp; &nbsp; &nbsp;2</span><span style="font-size: 10pt;">）参与方式：在智塔网，我们的参与形式是合作创作，而不同于传统的威客形式。作为参与者，您可以在其他人的创意基础上进行完善，寻找合作创作的乐趣；也可以通过首先&ldquo;发布创意&rdquo;，来最早贡献您的创意开局，让其他人在您的创意基础上继续发挥。一个小小的思想的火花可能就会为一个品牌带来让人终身难忘的印记，而您就可能是他的创造者之一。</span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt;">&nbsp; &nbsp; &nbsp;3</span><span style="font-size: 10pt;">）悬赏分配方式：传统的威客网站多采用投标制，一人或多人分等级中奖。而决定中奖人的为发标者。智塔网独创的分配计算方式，由悬赏发布方和智塔合作确定候选人，而候选人接受所有注册用户的投票来决定最终的悬赏分配比例。每一个候选人的贡献都是透明的，做到尽量让每一步都可以透明公开，并可以让所有的个人用户可以能够参与其中，加入到分配的计算中来。（具体的悬赏分配方式见下述的&ldquo;全新的悬赏分配方式&rdquo;）</span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">我们的理念<span lang="EN-US">:</span></span></strong><strong><span lang="EN-US" style="font-family: 宋体;">&nbsp;</span></strong><span style="font-size: 10.5pt; font-family: 华文行楷; color: red;">&ldquo;</span><span style="font-size: 10.5pt; font-family: 华文行楷; color: red;">不做总统，就做广告人！&rdquo;</span><span style="font-size: 10pt;">罗斯福总统的这句名言曾让无数广告人心潮澎湃。广告人，可以天马行空；广告人，可以异想天开。让自己的创意在短短的几十秒内尽情展现。智塔网的理念就是打造一个让每个人都能亲身参与到广告创作及其推广当中的平台：在这里，您无需是专业的广告人士，只需要有一颗热情创作的心。</span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">为什么我要参与<span lang="EN-US">:</span></span></strong><strong><span lang="EN-US" style="font-size: 14pt; font-family: 华文行楷;"><br /></span></strong><strong><span lang="EN-US" style="font-size: 14pt; font-family: 宋体;">&nbsp;</span></strong><strong><span lang="EN-US" style="font-size: 14pt; font-family: 华文行楷;">&nbsp;&nbsp;</span></strong><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">物质奖励</span></strong></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt;">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style="font-size: 10pt;">广告创意的需求发布方（通常为一个<span lang="EN-US">/</span>多个品牌的企业所有者）会在智塔网，以悬赏的方式来进行广告创意的征集。参与者只要有份参与的创意被选中，就有机会成为候选人，候选人会按照三方（发布方，注册用户和智塔网）的评分和投票来决定奖金的分配比例。投票结束后，悬赏会按照计算比例自动打入对应的参与者的积分帐户。（候选人获得的悬赏积分会在帐户的积分中查询到，并可以随时兑换成等值货币。）</span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><strong><span lang="EN-US" style="font-size: 14pt; font-family: 宋体; font-weight: normal;">&nbsp; </span></strong><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">精神追求<span lang="EN-US">:</span></span></strong></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; 1</span><span style="font-size: 10pt;">）发挥自己的创意，让广告变得更有创造性和乐趣，让那些千篇一律、甚至令人厌恶的广告见鬼去吧</span><span style="font-size: 10pt;">。</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;"><br /></span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; 2</span><span style="font-size: 10pt;">）</span><span style="font-size: 10pt;">在多媒体看到自己参与创作的作品</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">,</span><span style="font-size: 10pt;">是件很爽的事情。</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;"><br /></span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; 3</span><span style="font-size: 10pt;">）与他人合作，贡献并分享你的创意</span><span style="font-size: 10pt;">。</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;"><br /></span><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">我该如何参与<span lang="EN-US">:</span></span></strong><strong><span lang="EN-US" style="font-size: 14pt; font-family: 华文行楷;"><br /></span></strong><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; 1</span><span style="font-size: 10pt;">）</span><span style="font-size: 10pt;">参与智塔网的广告创意文案非常简单，只要您在首页或者项目页寻找到您乐于参与的关于品牌或产品的创意文案需求，作为注册用户，就可以在页面中找到&ldquo;编辑&rdquo;或者&ldquo;发布创意&rdquo;，然后就可以用文字与他人一同分享您的独特创意文案了。您可以在其他人的创意基础上进行完善，寻找合作创作的乐趣；也可以通过&ldquo;发布创意&rdquo;，来首先贡献您的创意，让其他人在您的创意基础上继续发挥。</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;"><br /></span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; 2</span><span style="font-size: 10pt;">）</span><span style="font-size: 10pt;">除了参与创意，在项目进入投票期的三天里，您可以为进入投票候选人名单的优秀创意贡献者投票（每人限定</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">3</span><span style="font-size: 10pt;">票），表达对其突出贡献的支持。</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;"><strong><span style="font-size: 10.5pt; font-family: 华文行楷;">您的支持可以改变悬赏分配的比例哦！</span></strong><br />&nbsp; &nbsp; &nbsp; &nbsp; 3</span><span style="font-size: 10pt;">）</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">SNS</span><span style="font-size: 10pt;">的交流与分享：在其他</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">SNS</span><span style="font-size: 10pt;">渠道来与朋友们分享您的创意。我们会尽快地开通更多的</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">SNS</span><span style="font-size: 10pt;">接口，让您更轻松的和朋友分享您的创意。</span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">全新的悬赏分配方式<span lang="EN-US">:</span></span></strong></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;</span><span style="font-size: 10pt;">智塔网设计了全新的悬赏分配方式，尽量做到公平公正和透明，并让所有用户都可以亲身参与到悬赏的分配中来。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;1</span><span style="font-size: 10pt;">）发布方：在创意进行阶段和打分期，为参与者的版本打分数（</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1-10</span><span style="font-size: 10pt;">），分数越高代表对参与者的创意越满意；（没有打分数，则代表打分方认为该版本并没有特别的贡献）</span></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;2</span><span style="font-size: 10pt;">）发布方：在创意结束后的打分期中（结束后的</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1</span><span style="font-size: 10pt;">天，在打分期中，创意是关闭的，无法继续参与），在多个创意方案中选定一个方案；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;3</span><span style="font-size: 10pt;">）智塔网：在创意进行阶段和打分期，为参与者的版本打分数（</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1-10</span><span style="font-size: 10pt;">）；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;4</span><span style="font-size: 10pt;">）您参与的这个创意方案被发布方选中，同时在这个方案中您发布的版本的分数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&gt;0</span><span style="font-size: 10pt;">（即发布方或智塔给出的分数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&gt;0</span><span style="font-size: 10pt;">），则您就会作为投票的候选人出现在投票列表中，最后的奖金分配必然有您的一杯羹；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;5</span><span style="font-size: 10pt;">）方案在打分期后，进入到</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">3</span><span style="font-size: 10pt;">天的投票期。在投票期中，所有的注册用户都可以为心仪的候选人投上自己的票数（不多于</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">3</span><span style="font-size: 10pt;">票），来表示自己对候选人的支持；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;6</span><span style="font-size: 10pt;">）投票结束后，进行最后的悬赏分配，按照</span><span style="font-size: 10pt;">注册用户投票</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">/</span><span style="font-size: 10pt;">发布方打分</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">/</span><span style="font-size: 10pt;">智塔打分</span><span style="font-size: 10pt;">比例为</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp;2</span><span style="font-size: 10pt;">：</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">2</span><span style="font-size: 10pt;">：</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1</span><span style="font-size: 10pt;">来计算最后的悬赏积分。例如：候选人</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">A</span><span style="font-size: 10pt;">在总数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">100</span><span style="font-size: 10pt;">票的注册用户投票中获得了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">30</span><span style="font-size: 10pt;">票；在企业的中选创意打分中，获得了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">20</span><span style="font-size: 10pt;">分（企业在该创意中总共给出了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">50</span><span style="font-size: 10pt;">分至数名候选人）；在智塔的该中选创意的打分中，获得了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">10</span><span style="font-size: 10pt;">分（智塔在该创意中总共给出了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">50</span><span style="font-size: 10pt;">分至数名候选人）。而该创意悬赏方案的悬赏金额为</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">5000</span><span style="font-size: 10pt;">元，则</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">A</span><span style="font-size: 10pt;">获得的悬赏积分计算如下：</span></p>\n<p class="MsoNormal" style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; [(30/100)X2+(20/50)X2+(10/50)X1]/(2+2+1)X5000=1600元</span></p>\n<p>&nbsp;</p>', 0.0, 0.0, '', '', 0, 4, 1392050526);
INSERT INTO `version` (`id`, `num`, `project`, `wit`, `name`, `content`, `score_witower`, `score_company`, `comment_witower`, `comment_company`, `deleted`, `user`, `time`) VALUES
(5, 1, 5, 5, 'TELL ME WHY?', '<p><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">智塔平台能为合作企业做什么</span></strong></p>\n<p class="MsoNormal" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri; mso-bidi-font-weight: bold;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-weight: bold;">品牌共创，让您的品牌更加深入人心：智塔网是一个探索新型互动广告的网站平台。在这里，我们借由互联网的广阔平台，提供一个通过互动广告文案及其他方式，让企业能够在品牌创立及传播中与广大的用户（及潜在用户）进行有效的互动沟通</span><span lang="EN-US" style="font-size: 9.0pt; mso-bidi-font-weight: bold;">--</span><span style="font-size: 12.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; color: red; mso-font-kerning: 0pt;">&ldquo;亲身参与的，才是最好的&rdquo;</span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-weight: bold;">，进而让消费者融入到品牌建设中来，成为您的品牌建设中最有力的一环，让&ldquo;我的品牌&rdquo;成为&ldquo;我们的品牌&rdquo;。</span></p>\n<p class="MsoNormal" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri; mso-bidi-font-weight: bold;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; &nbsp;</span></span><!--[endif]--><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-weight: bold;">创造更优秀的广告及相关作品，让人们对品牌拥有更加深刻的记忆。利用互联网用户无穷的想象力</span><span style="font-size: 9.0pt; font-family: 宋体; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt;">来丰富品牌的广告形象，改变千篇一律的广告内容，让精彩独特的广告来助力您的品牌企业。</span></p>\n<p class="MsoNormal" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri; mso-bidi-font-weight: bold;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; &nbsp;</span></span><!--[endif]--><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-weight: bold;">参与者更乐于对自身参与的广告及相关作品进行心甘情愿的传播与分享，让自媒体浪潮对企业的品牌宣传起到推波助澜的效果。</span></p>\n<p class="MsoNormal" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri; mso-bidi-font-weight: bold;">4.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-weight: bold;">培养潜在用户及开发更多的产品需求：通过多种互动广告方式让更多的参与者成为潜在用户，并认识和了解品牌；同时</span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">用户或潜在用户通过广告创意的互动，可以从某种角度让发布企业更加了解其内在的更多的消费需求，从而帮助企业发掘更多的产品以及周边。</span></p>\n<p class="MsoNormal" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri; mso-bidi-font-weight: bold;">5.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; &nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-weight: bold;">企业文化延伸：一个品牌的广告，承载着其拥有者对自身品牌的利益认知和情感归属，而让更多的人参与到品牌建设中，会让其在实现自我的同时，更加的理解和认可品牌的价值观，个性，品味等；同时积极的参与全新的商业模式也有助于树立企业</span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">勇于创新和尝试的形象，为企业的无形资产增加重要的砝码。</span></p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">为什么广告文案创意的征集要在智塔网进行</span></strong></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -18.0pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">多视角：普通的广告一般都是由少数的广告业内人士创意，这些人生活圈子单一而重叠，策划的品牌广告很难能够在深层次打动广大的消费者，因此更多的是以大规模的推广渠道来&ldquo;霸占&rdquo;消费者的视线。这种方法的效果更多的是建立在企业方的资金推动上。而智塔网提供的互动广告文案方式，是让更多的人能够以参与广告文案的方式来了解并宣传品牌，其发掘的独特视角能够引起更多人的共鸣，同时让更多的人牢记企业的品牌及形象。同时本身文案的悬赏，对于企业品牌来说就是一次良好的宣传推广。</span></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">高频率并应时：企业可以针对不同的时期及变化，随时发布创意需求；同时参与者也会根据不同的时令（如各种节日或发生的令人关注的事件等）以及当时的流行（包括语言、趋势等）为需求方进行当令应时的创作，让创作出的推广作品更加的贴合时代，更容易让广大的消费者铭记。</span></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><span lang="EN-US" style="text-indent: -18pt; font-size: 9pt;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="text-indent: -18pt; font-size: 9pt; font-family: 宋体;">互动性：智塔网的创作形式让更多的人参与到了广告的创作当中，有了互动性，才行让品牌更加的深入人心。一个让人印象深刻的广告必然是有参与感的，或反之亦然。</span></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;">4.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; &nbsp;</span></span><!--[endif]--><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">企业形象：全新的商业模式有助于帮助树立企业勇于创新和尝试的形象，为企业的无形资产增加重要的砝码。</span></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;">5.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">了解客户与潜在客户：用户或潜在用户通过广告创意的互动，可以让企业更加了解其内在的更多的消费需求，从而帮助企业发掘更多的产品以及周边。</span></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -18.0pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;">&nbsp;6.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">低成本，多渠道：企业在智塔网发布的创意文案需求，所需的悬赏金额可控而且金额数量无需巨大，一定数量的悬赏就可以吸引不少的用户参与。同时作品做成多媒体作品后，企业也可以更加多的尝试目前新兴的自媒体中进行推广，比如微博、微信等，这些渠道推广费用较低且可控，同时参与者的成就感也会让其在自身能力范围内的自媒体内，协助推广其参与的企业品牌广告。当然如果企业能力允许，在一些较高费用但是受众更广的渠道（如卫视等）发布，同时创造一些讨论话题的方式，也不失为一个快速推广品牌的好方法。</span></p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">企业如何操作</span></strong></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;"><span style="font-family: Verdana, Arial, Helvetica, sans-serif; text-indent: -24px;">1.&nbsp;</span><span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><!--[endif]--><span style="font-size: 9pt;">品牌授权：发布创意悬赏的企业应该合法拥有其所持的品牌，同时授权在智塔网上进行使用。</span></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;"><span style="font-family: Verdana, Arial, Helvetica, sans-serif;">2</span>.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9pt;">日常管理：合作企业方在智塔网拥有独立的企业管理帐号，可以进行如下的日常管理行为，而智塔网将尽力协助企业管理并给予必要和大力的支持：</span></p>\n<p class="MsoListParagraph" style="margin-left: 63.0pt; text-indent: -18.75pt; mso-char-indent-count: 0; mso-list: l0 level2 lfo1;"><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;"><span style="font-family: Calibri, sans-serif; font-size: 9pt;">a</span>.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; &nbsp;&nbsp;</span></span><!--[endif]--><span style="font-size: 9pt;">发布项目：按照企业需要，随时在智塔网的平台上发布所需的广告创意需求项目，征集创意文案。在发布项目中，企业根据自身需要，设定悬赏金额（用于悬赏的积分由企业在智塔网存入的货币来决定，具体额度由双方的具体合同来规定）、悬赏期限（一般设定为</span><span lang="EN-US" style="font-size: 9.0pt;">7-15</span><span style="font-size: 9pt;">天）、以及所要征集的广告方案的要求（如节日相关、校园相关、特定人群等）。</span></p>\n<p class="MsoListParagraph" style="margin-left: 63.0pt; text-indent: -18.75pt; mso-char-indent-count: 0; mso-list: l0 level2 lfo1;"><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;"><span style="font-family: Calibri, sans-serif; font-size: 9pt;">b</span>.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; &nbsp;&nbsp;</span></span><!--[endif]--><span style="font-size: 9pt;">管理项目：在项目进行期中，对项目进行必要的修订与管理。</span></p>\n<p class="MsoListParagraph" style="margin-left: 63.0pt; text-indent: -18.75pt; mso-char-indent-count: 0; mso-list: l0 level2 lfo1;"><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;"><span style="font-family: Calibri, sans-serif; font-size: 9pt;">c</span>.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9pt;">日常评分：为创意进行中的创意版本评分，对于好的创意版本给予一定的评分（</span><span lang="EN-US" style="font-size: 9.0pt;">1-10</span><span style="font-size: 9pt;">分，分数越高意味着企业越欣赏该名参与者的版本；当然，对于几乎没有贡献的版本，可以不给予分数），该分数将会按照比例来计算参与者的贡献，并分配悬赏金额。</span></p>\n<p class="MsoListParagraph" style="margin-left: 63.0pt; text-indent: -18.75pt; mso-char-indent-count: 0; mso-list: l0 level2 lfo1;"><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;"><span style="font-family: Calibri, sans-serif; font-size: 9pt;">d</span>.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9pt;">选定方案：在悬赏结束后的一天内，企业可以自由在几个方案中选定心仪的一个作为最终中选方案，那么意味着中选方案中的，得到企业（和</span><span lang="EN-US" style="font-size: 9.0pt;">/</span><span style="font-size: 9pt;">或智塔）评分大于零的参与者就会作为候选人出现在最后参与分配奖金的名单中。如果企业没有正式的选择一个创意作为最终项目方案，那么系统会按照之前企业用户和智塔网为用户所打的分数，自动选择版本总分数最高的那个方案作为最终方案。</span></p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">联系我们</span></strong></p>\n<p class="MsoNormal" style="margin-left: 28.25pt; mso-para-margin-left: 2.69gd;"><span style="font-size: 9pt;">智塔网愿竭诚为您服务，如果您对智塔网有兴趣或有任何疑问，请按照如下的方式与我们联系：</span></p>\n<p class="MsoNormal" style="margin-left: 28.25pt; mso-para-margin-left: 2.69gd;"><span lang="EN-US" style="font-size: 9.0pt; mso-bidi-font-weight: bold;">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style="font-size: 9pt;">联系电话：</span><span lang="EN-US" style="font-size: 9pt; font-family: Calibri, sans-serif;">13661408983</span></p>\n<p class="MsoNormal" style="margin-left: 28.25pt; mso-para-margin-left: 2.69gd;"><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: 宋体; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA; mso-bidi-font-weight: bold;">&nbsp; &nbsp; &nbsp; &nbsp; E-mail:Contactus@witower.com</span></p>\n<p style="margin: 0in; font-family: 宋体; font-size: 10.5pt;">&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>', 0.0, 0.0, '', '', 0, 4, 1392210872);
INSERT INTO `version` (`id`, `num`, `project`, `wit`, `name`, `content`, `score_witower`, `score_company`, `comment_witower`, `comment_company`, `deleted`, `user`, `time`) VALUES
(6, 1, 6, 6, ' KNOW HOW!', '<p><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">企业如何申请智塔网企业用户及通过认证：</span></strong>&nbsp;</p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">在智塔网首页的页面，您可以找到&ldquo;注册&rdquo;，在注册页面中选择&ldquo;注册为企业用户&rdquo;并选择输入一定的公司简介，并&ldquo;同意&lsquo;智塔网企业用户协议&rsquo;&rdquo;。智塔网会在进行必要的审核和核实后，为您的用户升级成为企业用户。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">您也可以通过&ldquo;联系我们&rdquo;中的联系方式和智塔网取得联系，由客服人员为您提供必要的指导和帮助，并直接注册成为企业用户。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">注册成为企业会员后，使用企业用户账号登录后，可以在页面上方找到&ldquo;管理&rdquo;的链接，点击后就可以进入到企业后台的管理页面，用于发布创意项目。</span></p>\n<p><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">如何获得悬赏积分用于发布项目<span lang="EN-US">:</span></span></strong></p>\n<p style="margin: 0cm; margin-bottom: .0001pt; text-indent: 22.5pt; mso-char-indent-count: 2.5;"><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&nbsp;&nbsp;</span><span lang="EN-US" style="font-size: 10.0pt;"><span style="font-size: 9pt; text-indent: 22.5pt;">注册的企业用户，可以用于悬赏的积分，由企业在智塔网存入的货币来决定，具体额度由双方的具体合同来规定。</span></span></p>\n<p><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">为什么要维护产品信息</span></strong></p>\n<p style="margin-top: 0cm; margin-right: 0cm; margin-bottom: .0001pt; margin-left: 1.0cm; mso-para-margin-top: 0cm; mso-para-margin-right: 0cm; mso-para-margin-bottom: .0001pt; mso-para-margin-left: 2.7gd;"><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">在智塔网发布项目，需要先维护产品信息，然后在产品的项下来发布创意项目。这样的设置有利于企业对于项目的管理；也让参与创意的用户可以通过产品及相关介绍更加了解其参与的项目，更有利于创作出贴近品牌产品的作品。</span>&nbsp;</p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">如何发布和维护项目</span></strong></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">发布新项目：在企业用户的后台管理页面的左侧工具栏中，您可以找到&ldquo;项目管理&rdquo;，点击后，就可以&ldquo;增加新项目&rdquo;：</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">a.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">项目名称：用于显示在首页及其他页面上的，关于该创意项目的简要描述。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">b.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">所属产品：该创意属于哪个产品，产品的信息会显示在项目创意的下方，让参与者更加的了解项目的及产品的信息。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">c.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">项目图片：项目所配发的相关图片。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">d.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">项目描述：对项目的要求描述，如&ldquo;该项目征集视频</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">30</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">秒左右的广告创意，要求与情人节相关，温馨浪漫或有趣搞笑，目标群体为上海地区的高校学生，可以使用上海话&rdquo;。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">e.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">创意征集：为征集的创意的起始和结束日期：默认的起始日期为当前日期</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">+1</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">天，即目前发布的项目进入准备期；当然发布的企业用户可以将起始日期更改为当前日期，即该项目马上进入到创意的征集中。结束日期为开始日期之后的</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">7-15</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">天的范围内，具体日期由企业用户自行设定。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">f.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">投票评选：投票的起始和结束日期：该起始日期由系统自动按照创意征集的结束日期进行计算，一般为创意征集的结束日期</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">+2</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">天。在创意征集和投票评选之间的一天为打分期及选定创意方案期，即这一天企业需要为创意的多种方案中选择一个最心仪的方案用于最后的投票评定；如果企业没有选择，则系统会按照日常的版本打分中，自动选择版本总分数最高的一个方案作为最终的入选方案；投票结束日期为投票起始日期</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">+3</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">天。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">g.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">悬赏金额：用于悬赏的金额。该金额上限由企业用户的悬赏积分来决定；在该范围内，企业可以自由决定每个项目要使用的悬赏积分；在企业发布项目后，该部分的悬赏金额作为&ldquo;已冻结积分&rdquo;，在项目结束（即投票结束，积分记入相关的参与用户帐户）后，从企业用户的积分帐户中扣除。悬赏积分的使用情况，可以在企业的积分帐户中进行查询。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">h.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">关键词：企业用户可以设定若干关键词用于参与者快速的搜索，以便更好的找到发布的相关项目。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">维护项目：企业用户可以在项目进行时随时修改一些必要的信息，如标题，内容等。至于悬赏金额和悬赏期限等内容在企业发布后无法自行更改，如果确实需要进行更正，则企业用户可以联系智塔网的客服人员，由其在核实后进行必要的更正。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><span lang="EN-US" style="text-align: justify; text-indent: -11.05pt; font-size: 9pt; font-family: Calibri, sans-serif;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="text-align: justify; text-indent: -11.05pt; font-size: 9pt;">在项目进行中，企业可以对参与的内容进行一些必要的修正和管理，对出现一些不合企业要求的内容，如不合法的言论（涉及政治，色情等的言论）或</span><span lang="EN-US" style="text-align: justify; text-indent: -11.05pt; font-size: 9pt; font-family: Calibri, sans-serif;">/</span><span style="text-align: justify; text-indent: -11.05pt; font-size: 9pt;">和对企业不利及不当的内容时，企业可以通过编辑方案来更正，同时可以向智塔相关人员投诉，对发布者进行相应的处理。</span><span style="font-family: Calibri, sans-serif; font-size: 10.5pt;">&nbsp;</span></p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">如何查看创意</span></strong></p>\n<p>&nbsp;<span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">查看参与者的创意版本，可以通过如下几个方法：</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">1. &nbsp;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">创意项目页面，每个创意方案的&ldquo;编辑&rdquo;功能后面，都有&ldquo;版本&rdquo;链接，点击后，就可以在页面中查看每个参与用户对该创意方案的版本贡献；点击每个版本，还可以查看到前后版本之前的差别（系统会使用不同的颜色来进行差别的标识）。该查看方式对所有用户开放。在项目进行中，企业用户可以在该页面对每个版本进行日常的评分和评价。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">企业用户的后台管理</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&mdash;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">项目管理</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&mdash;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">创意</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&mdash;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">查看，进入新页面后，点击标题后，就可以对版本进行查看、对比和评分等操作。右侧的工具栏中，可以在不同的版本之间进行切换，并进行评分和评价。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; </span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">企业用户的后台管理</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&mdash;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">项目管理</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&mdash;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">创意</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&mdash;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">查看，进入新页面后，点击版本数</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">--</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">查看，在出现的版本列表中，可以随意的勾选版本进行对比，并进行评分和评价。</span></p>\n<p class="MsoNormal" style="text-align: left;"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">如何对比版本及评分</span></strong></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt;">企业用户在每个查看创意的页面中，都可以进行各个版本的对比，以便确定每个版本所有者对该创意的贡献，同时可以对该版本的贡献进行评定（包括评分和评价）。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt;">评分为</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">1-10</span><span style="font-size: 9.0pt;">分，分数越高表示评分者对该版本的创作内容评价越高；不给予分数，表示评分者认为该版本的创作内容对创意方案并无明显的贡献。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt;">所有的用户都可以在项目页的版本页面中查看企业用户给出的分数和评语，也可以查看参与者的版本对比，即企业和智塔网对参与者的评价与评分都处于公开透明的状态，接受所有用户的监督。</span></p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">如何选择中选的创意</span></strong></p>\n<p style="margin-top: 0cm; margin-right: 0cm; margin-bottom: .0001pt; margin-left: 42.55pt; text-align: justify; text-justify: inter-ideograph; text-indent: -11.05pt; mso-list: l5 level1 lfo7;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 9.0pt;">在项目结束并进入投票期之前的一天的打分期中，企业用户可以在项目的若干个创意中选择一个作为中选的方案。</span></p>\n<p style="margin-top: 0cm; margin-right: 0cm; margin-bottom: .0001pt; margin-left: 42.55pt; text-align: justify; text-justify: inter-ideograph; text-indent: -11.05pt; mso-list: l5 level1 lfo7;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 9.0pt;">每个查看创意的页面中，都可以选定中选创意；在打分期中，可以取消选定的创意并进行重新选择</span></p>\n<p style="margin-top: 0cm; margin-right: 0cm; margin-bottom: .0001pt; margin-left: 42.55pt; text-align: justify; text-justify: inter-ideograph; text-indent: -11.05pt; mso-list: l5 level1 lfo7;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 9.0pt;">目前每个项目只能选定一个中选创意。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">4. &nbsp;&nbsp;</span><span style="font-size: 9.0pt;">如果企业用户没有正式的选择一个创意作为最终方案，那么系统会按照之前日常中企业和智塔对创意版本的所打的分数，自动选择版本总分数最高的那个方案作为最终方案。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;">&nbsp;</p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">悬赏积分如何分配</span></strong></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style="font-size: 9pt;">智塔网设计了全新的悬赏分配方式，尽量做到公平公正和透明，并让所有用户都可以亲身参与到悬赏的分配中来。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt;">发布方：在创意进行阶段和打分期，为参与者的版本打分数（</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">1-10</span><span style="font-size: 9.0pt;">），分数越高代表对参与者的创意版本越满意；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt;">发布方：在创意结束后的打分期中（结束后的</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">1</span><span style="font-size: 9.0pt;">天，在打分期中，创意是关闭的，无法继续参与），在若干创意方案中选定一个中选方案；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; </span></span><span style="font-size: 9.0pt;">智塔网：在创意进行阶段和打分期，为参与者的版本打分数（</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">1-10</span><span style="font-size: 9.0pt;">）；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><span lang="EN-US" style="text-indent: -11.05pt; font-size: 9pt; font-family: Calibri, sans-serif;">4.&nbsp;</span><span style="text-indent: -11.05pt; font-size: 9pt;">用户参与的创意方案被发布方选中，同时在这个方案中用户发布的版本的分数</span><span lang="EN-US" style="text-indent: -11.05pt; font-size: 9pt; font-family: Calibri, sans-serif;">&gt;0</span><span style="text-indent: -11.05pt; font-size: 9pt;">（即发布方或智塔给出的分数</span><span lang="EN-US" style="text-indent: -11.05pt; font-size: 9pt; font-family: Calibri, sans-serif;">&gt;0</span><span style="text-indent: -11.05pt; font-size: 9pt;">），则用户就会作为投票的候选人出现在投票列表中，接受注册用户的投票；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">5.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt;">方案在打分期后，进入到</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">3</span><span style="font-size: 9.0pt;">天的投票期。在投票期中，所有的注册用户都可以为心仪的候选人投上自己的票数（不多于</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">3</span><span style="font-size: 9.0pt;">票），来表示自己对候选人的支持；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">6.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9pt;">投票结束后，进行最后的悬赏分配，按照</span><span style="font-size: 9pt;">注册用户投票</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;">/</span><span style="font-size: 9pt;">发布方打分</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;">/</span><span style="font-size: 9pt;">智塔打分</span><span style="font-size: 9pt;">比例为</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;"> 2</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">：</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">2</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">：</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">1</span><span style="font-size: 9pt;">来计算最后的悬赏积分。例如：候选人</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;">A</span><span style="font-size: 9pt;">在总数</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">100</span><span style="font-size: 9pt;">票的注册用户投票中获得了</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">30</span><span style="font-size: 9pt;">票；在企业的中选创意打分中，获得了</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">20</span><span style="font-size: 9pt;">分（企业在该创意中总共给出了</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">50</span><span style="font-size: 9pt;">分至数名候选人）；在智塔的该中选创意的打分中，获得了</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">10</span><span style="font-size: 9pt;">分（智塔在该创意中总共给出了</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">50</span><span style="font-size: 9pt;">分至数名候选人）。而该创意悬赏方案的悬赏金额为</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">5000</span><span style="font-size: 9pt;">元，则</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;">A</span><span style="font-size: 9pt;">获得的悬赏积分计算如下：</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><span style="font-size: 9pt;">[(30/100)X2+(20/50)X2+(10/50)X1]/(2+2+1)X5000=1600 元</span></p>', 0.0, 0.0, '', '', 0, 4, 1392352212);

CREATE TABLE IF NOT EXISTS `version_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `version` int(11) NOT NULL,
  `content` varchar(255) NOT NULL,
  `user` int(11) NOT NULL,
  `time` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `version` (`version`),
  KEY `user` (`user`),
  KEY `time` (`time`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

CREATE TABLE IF NOT EXISTS `wit` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `content` text NOT NULL,
  `project` int(11) NOT NULL,
  `user` int(11) NOT NULL,
  `time` int(11) NOT NULL,
  `selected` tinyint(1) NOT NULL,
  `deleted` tinyint(1) NOT NULL,
  `latest_version` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `project` (`project`),
  KEY `user` (`user`),
  KEY `time` (`time`),
  KEY `latest_version` (`latest_version`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=7 ;

INSERT INTO `wit` (`id`, `name`, `content`, `project`, `user`, `time`, `selected`, `deleted`, `latest_version`) VALUES
(1, '智塔网网络服务使用协议', '<p style="margin: 0in; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 16pt;"><span lang="zh-CN" style="font-family: 宋体; font-size: 9pt; font-weight: bold;">《</span><span lang="en-US" style="font-family: 宋体; font-size: 9pt; font-weight: bold;">&nbsp;</span><span lang="zh-CN" style="font-family: 宋体; font-size: 9pt; font-weight: bold;">智塔网络服务使用协议</span><span lang="en-US" style="font-family: 宋体; font-size: 9pt; font-weight: bold;">&nbsp;</span><span lang="zh-CN" style="font-family: 宋体; font-size: 9pt; font-weight: bold;">》</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">智塔网（以下简称"本网站"）依据《智塔网服务协议》（以下简称"本协议"）的规定提供服务，本协议具有合同效力。注册会员时，请您认真阅读本协议，审阅并接受或不接受本协议（未成年人应在法定监护人陪同下审阅）。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">若您已经注册为本网站会员，即表示您已充分阅读、理解并同意自己与本网站订立本协议，且您自愿受本协议的条款约束。本网站有权随时变更本协议并在本网站上予以公告。经修订的条款一经在本网站的公布后，立即自动生效。如您不同意相关变更，必须停止使用本网站。本协议内容包括协议正文及所有智塔网已经发布的各类规则。所有规则为本协议不可分割的一部分，与本协议正文具有同等法律效力。一旦您继续使用本网站，则表示您已接受并自愿遵守经修订后的条款。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt;"><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">第一条</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">会员资格&nbsp;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">1、只有符合下列条件之一的自然人或法人才能申请成为本网站会员，可以使用本网站的服务；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、年满十八岁，并具有民事权利能力和民事行为能力的自然人；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、无民事行为能力人或限制民事行为能力人应经过其监护人的同意；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">C、根据中国法律、法规、行政规章成立并合法存在的机关、企事业单位、社团组织和其他组织。无法人资格的单位或组织不当注册为本网站会员的，其与本网站之间的协议自始无效，本网站一经发现，有权立即终止对该会员的服务，并追究其使用本网站服务的一切法律责任。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">2、会员需要根据网站提示，提供明确的联系地址和联系电话，并提供真实姓名或名称以及其他必要的信息。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt;"><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">第二条</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">会员的权利和义务</span><span lang="zh-CN" style="font-family: 宋体;">&nbsp;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">1</span><span lang="zh-CN">、所有会员须自行负责自己的会员账号和密码，且须对在会员账号密码下发生的所有活动（包括但不限于参与创意需求文案、网上点击同意各类协议、规则、参加创意项目投票、申请提取所获取的悬赏金额等行为）承担责任。会员有权根据需要更改登录和账户提现密码。因会员的过错导致的任何损失由会员自行承担，该过错包括但不限于：不按照交易提示操作，未及时进行交易操作，遗忘或泄漏密码，密码被他人破解，您使用的计算机被他人侵入等；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">2</span><span lang="zh-CN">、会员应当向本网站提供真实准确的注册信息，包括但不限于真实姓名、身份证号、正确的银行帐号（用于领取申请的悬赏）、联系电话、地址、邮政编码等。保证本网站可以通过上述联系方式与自己进行联系。同时，会员也应当在相关资料实际变更时及时更新有关注册资料；会员在进行悬赏兑现时应提供准确的</span><span lang="en-US">,</span><span lang="zh-CN">与之相匹配的银行信息</span><span lang="en-US">,</span><span lang="zh-CN">保证本网站能及时为用户兑现</span><span lang="en-US">;</span><span lang="zh-CN">若因为会员的疏忽或其他会员自身原因造成的会员损失</span><span lang="en-US">,</span><span lang="zh-CN">本网站不负担任何责任</span><span lang="en-US">;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">3</span><span lang="zh-CN">、会员不得以任何形式擅自转让或授权他人使用自己在本网站的会员帐号；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">4</span><span lang="zh-CN">、会员有义务确保在本网站上填写信息的真实、准确，无误导性；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">5</span><span lang="zh-CN">、会员在本网站网上平台，不得发布国家法律、法规、行政规章规定禁止的信息、侵犯他人知识产权或其他合法权益的信息、违背社会公共利益或公共道德的信息；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">6</span><span lang="zh-CN">、会员承诺自己在使用本网站实施的所有行为遵守法律、法规、行政规章和本网站的相关规定以及各种社会公共利益或公共道德。如有违反导致任何法律后果的发生，会员将以自己的名义独立承担相应的法律责任；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">7</span><span lang="zh-CN">、会员在本网站网上交易过程中如与其他会员因交易产生纠纷，可以请求本网站从中予以协调处理。会员如发现其他会员有违法或违反本协议的行为，可以向本网站举报；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">8</span><span lang="zh-CN">、会员应当自行承担因交易产生或取得的相关费用，并依法纳税；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">9</span><span lang="zh-CN">、会员不得使用以下方式登录网站或破坏网站所提供的服务：</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、以任何机器人软件、蜘蛛软件、爬虫软件、刷屏软件或其它自动方式访问或登录本网站；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、通过任何方式对本公司内部结构造成或可能造成不合理或不合比例的重大负荷的行为；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">C、通过任何方式干扰或试图干扰网站的正常工作或网站上进行的任何活动。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">10</span><span lang="zh-CN">、会员同意接收来自本网站的信息，包括但不限于活动信息、交易信息、促销信息等。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">会员应不时关注并遵守本站不时公布或修改的各类合法规则规定。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">本站保有删除站内各类不符合法律政策或不真实的信息内容而无须通知会员的权利。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">若会员未遵守以上规定的，本站有权作出独立判断并采取暂停或关闭会员帐号等措施。会员须对自己在网上的言论和行为承担法律责任。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #595959;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">第三条 本网站的权利和义务</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="zh-CN">1、目前本网站为会员提供一个信息交流平台，是企业会员发布自身品牌的广告创意需求和个人会员提供创意方案文案的一个交易市场，本网站对交易双方在法律规定的范围内进行协助和管理；本网站有义务对个人会员在本网站合法合理取得的悬赏积分进行兑换等值金额的货币；个人会员也可以使用获得的悬赏积分</span><span lang="en-US">,</span><span lang="zh-CN">购买本网站及其合作伙伴在智塔网进行出售的商品和服务</span><span lang="en-US">;</span><span lang="zh-CN">本网站有权对个人会员获得的悬赏积分进行追溯和核查</span><span lang="en-US">,</span><span lang="zh-CN">对不合理或者非法渠道获得的积分</span><span lang="en-US">,</span><span lang="zh-CN">本网站有权进行相关处理</span><span lang="en-US">,</span><span lang="zh-CN">并保留付诸法律的权利</span><span lang="en-US">;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">2、本网站有义务在现有技术水平的基础上努力确保整个网上交流平台的正常运行，尽力避免服务中断或将中断时间限制在最短时间内，保证会员在本网站上交流活动的顺利进行；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">3、本网站有义务对会员在注册和使用本网站信息平台中所遇到的与交易或注册有关的问题及反映的情况及时作出回复；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">4、本网站有权对会员的注册资料进行审查，对存在任何问题或怀疑的注册资料，本网站有权发出通知询问会员并要求会员做出解释、改正；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">5、会员因在本网站网上交易与其他会员产生纠纷的，会员将纠纷告知本网站，或本网站知悉纠纷情况的，经审核后，本网站有权通过电子邮件及电话联系向纠纷双方了解纠纷情况，并将所了解的情况通过电子邮件互相通知对方；会员通过司法机关依照法定程序要求本网站提供相关资料，本网站将积极配合并提供有关资料；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">6、因网上信息平台的特殊性，本网站没有义务对所有会员的交易行为以及与交易有关的其他事项进行事先审查，但如发生以下情形，本网站有权无需征得会员的同意限制会员的活动、向会员核实有关资料、发出警告通知、暂时中止、无限期中止及拒绝向该会员提供服务：</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、会员违反本协议或因被提及而纳入本协议的相关规则；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、存在会员或其他第三方通知本网站，认为某个会员或具体交易事项存在违法或不当行为，并提供相关证据，而本网站无法联系到该会员核证或验证该会员向本网站提供的任何资料；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">C、存在会员或其他第三方通知本网站，认为某个会员或具体交易事项存在违法或不当行为，并提供相关证据。本网站以普通非专业交易者的知识水平标准对相关内容进行判别，可以明显认为这些内容或行为可能对本网站会员或本网站造成财务损失或法律责任。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">7、根据国家法律、法规、行政规章规定、本协议的内容和本网站所掌握的事实依据，可以认定该会员存在违法或违反本协议行为以及在本网站平台上的其他不当行为，本网站有权无需征得会员的同意在本网站交易平台及所在网站上以网络发布形式公布该会员的违法行为，并有权随时作出删除相关信息、终止服务提供等处理；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="zh-CN">8、本网站依据本协议及相关规则，可以冻结并处置个人会员在本网站账户内的悬赏积分。被封号的个人会员如果对此存有异议</span><span lang="en-US">,</span><span lang="zh-CN">可以在对网站申请复议以便进一步处理；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="zh-CN">9、本网站有权在不通知会员的前提下，删除或采取其他限制性措施处理下列信息：包括但不限于反动</span><span lang="en-US">\\</span><span lang="zh-CN">色情</span><span lang="en-US">\\</span><span lang="zh-CN">存在欺诈等恶意或虚假内容；存在恶意删改他人信息或其他试图扰乱正常交易秩序因素；该信息违反公共利益或可能严重损害本网站和其他会员合法利益的。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">第四条 服务的中断和终止</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">1、若会员申请终止本网站服务，需经本网站审核同意，方可解除与本网站的协议关系，但本网站仍保留下列权利：</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、本网站有权在法律、法规、行政规章规定的时间内保留该会员的资料,包括但不限于以前的会员资料、交易记录等；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、若终止服务之前，该会员在本网站交易平台上存在违法行为或违反本协议的行为，本网站仍可行使本协议所规定的权利。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">2</span><span lang="zh-CN">、会员存在下列情况，本网站可以终止向该会员提供服务：</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、在会员违反本协议及相关规则规定时，本网站有权终止向该会员提供服务。本网站将在中断服务时通知会员。但该会员在被本网站终止提供服务后，再一次直接或间接或以他人名义注册为本网站会员的，本网站有权再次单方面终止为该会员提供服务；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、本网站发现会员注册资料中主要内容是虚假的，本网站有权随时终止为该会员提供服务；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">C、本协议终止或更新时，会员未确认新的协议的；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">D、其它本网站认为需终止服务的情况。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: red;"><span style="font-weight: bold;">第五条 本网站的责任范围</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">当会员接受该协议时，会员应当明确了解并同意∶</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">1、本网站不能随时预见到任何技术上的问题或其他困难。该等困难可能会导致数据损失或其他服务中断。本网站是在现有技术基础上提供的服务。本网站不保证以下事项∶</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">A、本网站将符合所有会员的要求；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">B、本网站不受干扰、能够及时提供、安全可靠或免于出错；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">C、本服务使用权的取得结果是正确或可靠的。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">2、会员经由本网站取得的建议和资讯，无论其形式或表现，绝不构成本协议未明示规定的任何保证；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US" style="font-weight: bold;">3</span><span lang="zh-CN" style="font-weight: bold;">、本网站只是为会员提供一个广告文案及相关的服务交易的平台，对于会员所发布的广告需求的相关产品的质量及其他涉及厂家的问题，本网站一律不负任何连带担保责任；&nbsp;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US" style="font-weight: bold;">4</span><span lang="zh-CN" style="font-weight: bold;">、本网站提供与其它互联网上的网站或资源的链接，会员可能会因此连接至其它运营商经营的网站，但不表示本网站与这些运营商有任何关系。其它运营商经营的网 站均由各经营者自行负责，不属于本网站控制及负责范围之内。对于存在或来源于此类网站或资源的任何内容、广告、物品或其它资料，本网站亦不予保证或负责。因使用或依赖任何此类网站或资源发布的或经由此类网站或资源获得的任何内容、物品或服务所产生的任何损害或损失，本网站不负任何直接或间接的责任；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">第六条 知识产权</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">1、本网站及本网站所使用的任何相关软件、程序、内容，包括但不限于作品、图片、档案、资料、网站构架、网站版面的安排、网页设计、经由本网站或广告商向会员呈现的广告或资讯，均由本网站或其它权利人依法享有相应的知识产权，包括但不限于著作权、商标权、专利权或其它专属权利等，受到相关法律的保护。未经本网站或权利人明示授权，会员保证不修改、出租、出借、出售、散布本网站及本网站所使用的上述任何资料和资源，或根据上述资料和资源制作成任何种类产品；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">2、本网站授予会员不可转移及非专属的使用权，使会员可以通过单机计算机使用本网站的目标代码（以下简称"软件"），但会员不得且不得允许任何第三方复制、修改、创作衍生作品、进行还原工程、反向组译，或以其它方式破译或试图破译源代码，或出售、转让"软件"或对"软件"进行再授权，或以其它方式移转"软件"之任何权利。会员同意不以任何方式修改"软件"，或使用修改后的"软件"；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="zh-CN">3、会员不得经由非本网站所提供的界面使用本网站</span><span lang="en-US">;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">4</span><span lang="zh-CN">、会员通过本网站提供的服务，参与在本网站公开区域的任何内容，视为用户同意将参与的广告创意作品的知识产权部分划归发布企业会员所有。就相应的企业会员对其参与的项目创意提供的悬赏奖励作为知识产权的支付费用：</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; A</span><span lang="zh-CN">、按照智塔网的规定，由企业会员、智塔网评分以及个人会员投票，来决定每个创意项目的奖金的分配比例；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; B</span><span lang="zh-CN">、个人会员在评分及投票中没有获得悬赏奖励的，即使参与创作也视为将其参与创作的知识产权转移给相应的企业用户；个人会员对获奖比例及名单等存在异议的，可以在网站内部的系统中进行投诉，由智塔网进行核实后确认，并给出最终解释和</span><span lang="en-US">/</span><span lang="zh-CN">或调整；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; C</span><span lang="zh-CN">、本网站拥有对此规定及相关比例等的解释权和修改权。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">5</span><span lang="zh-CN">、获得相应知识产权的企业会员有权通过如下方式行使该部分权利，但是，会员户声明保留该内容的著作权或该内容从其他网站转载而附带有原网站的著作权声明者除外：</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; A</span><span lang="zh-CN">、通过复制、发行、信息网络传播、改编、翻译、汇编、摄制、出租、展览、表演、放映、广播等方式使用该内容；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; B</span><span lang="zh-CN">、将该内容用于开发和运营各类电信增值业务及无线互联网业务（如短信、彩信、WAP、IVR、手机报、手机客户端等形式），对其中精彩的图文进行推广，推广方式包括智塔网站首页、子频道等链接推广或编辑出版等等；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; C</span><span lang="zh-CN">、推广市场授权他人使用该内容，授权第三方标示来源于智塔网站；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; D</span><span lang="zh-CN">、对任何三方侵犯该内容的知识产权的行为独立起诉进行维权。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">6</span><span lang="zh-CN">、个人用户参与部分的内容，若侵害到第三方的知识产权及相关合法权益，智塔网和</span><span lang="en-US">/</span><span lang="zh-CN">或企业用户不承担责任，并对因此侵权行为带来的损害，保留采取法律行动的权利。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">7</span><span lang="zh-CN">、若您认为本网站刊载的内容侵犯您的知识产权（包括著作权、商标专用权等）、名誉权、肖像权等合法权益，请您与本网站联系，并向提交书面通知，要求删除涉嫌侵权内容。该通知书应当包括下列内容：</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; A</span><span lang="zh-CN">、权利人的姓名（名称）、联系电话和通讯地址；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; B</span><span lang="zh-CN">、要求删除的涉嫌侵权内容的具体名称及其对应的网络地址；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US">&nbsp;&nbsp;&nbsp;&nbsp; C</span><span lang="zh-CN">、构成侵权的初步证明材料，包括权利人身份证明或企业营业执照、权利权属证明等。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">第七条 隐私权</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">1、信息使用：</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、本网站不会向任何人出售或出借会员的个人或法人信息，除非事先得到会员得许可；</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、本网站亦不允许任何第三方以任何手段收集、编辑、出售或者无偿传播会员的个人或法人信息。任何会员如从事上述活动，一经发现，本网站有权立即终止与该会员的服务协议，查封其账号。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">2、信息披露：会员的个人或法人信息将在下述情况下部分或全部被披露：&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、经会员同意，向第三方披露；&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、若会员是合法的知识产权使用权人并提起投诉，应被投诉人要求，向被投诉人披露，以便双方处理可能的权利纠纷；&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="zh-CN">C、根据法律的有关规定，或者行政、司法机关的要求，向第三方或者行政、司法机关披露</span><span lang="en-US">;</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">D、若会员出现违反中国有关法律或者网站规定的情况，需要向第三方披露；&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">E、为提供你所要求的产品和服务，而必须和第三方分享会员的个人或法人信息；&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">F、其它本网站根据法律或者网站规定认为合适的披露。&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">3、信息安全：&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">A、在使用本网站服务进行网上交易时，请会员妥善保护自己的个人或法人信息，仅在必要的情形下向他人提供；&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">B、如果会员发现自己的个人或法人信息泄密，尤其是会员账户或"支付账户管理"账户及密码发生泄露，请会员立即联络本网站客服，以便我们采取相应措施。&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">第八条 不可抗力</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">因不可抗力或者其他意外事件，使得本协议的履行不可能、不必要或者无意义的，双方均不承担责任。本合同所称之不可抗力意指不能预见、不能避免并不能克服的客观情况，包括但不限于战争、台风、水灾、火灾、雷击或地震、罢工、暴动、法定疾病、黑客攻击、网络病毒、电信部门技术管制、政府行为或任何其它自然或人为造成的灾难等客观情况。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">第九条 保密</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">双方保证在对讨论、签订、执行本协议中所获悉的属于对方的且无法自公开渠道获得的文件及资料（包括但不限于商业秘密、公司计划、运营活动、财务信息、技术信息、经营信息及其他商业秘密）予以保密。未经该资料和文件的原提供方同意，另一方不得向第三方泄露该商业秘密的全部或者部分内容。但法律、法规、行政规章另有规定或者双方另有约定的除外。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: red;"><span style="font-weight: bold;">第十条 争议解决方式</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">1、本协议及其规则的有效性、履行和与本协议及其规则效力有关的所有事宜，将受中华人民共和国法律管辖，任何争议仅适用中华人民共和国法律；</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">2、</span><span style="font-weight: bold;">如因智塔网违反有关法律、法规或本协议项下的任何条款而给会员造成损失，智塔网同意承担由此造成的损害赔偿责任。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span style="font-weight: bold;">3、会员</span><span style="font-weight: bold;">同意保障和维护智塔网及其他会员的利益，如因会员违反有关法律、法规或本协议项下的任何条款而给智塔网或任何其他第三人造成损失，会员同意承担由此造成的损害赔偿责任。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="en-US" style="font-weight: bold;">4</span><span lang="zh-CN" style="font-weight: bold;">、</span><span lang="zh-CN" style="font-weight: bold;">如双方就本协议内容或其执行发生任何争议，双方应尽量友好协商解决；协商不成时，任何一方均可向智塔网注册所在地的人民法院提起诉讼。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">&nbsp;</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;"><span lang="zh-CN" style="font-weight: bold;">第十一条</span><span lang="zh-CN" style="font-weight: bold;">其他规定</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">1、本协议构成双方对本协议之约定事项及其他有关事宜的完整协议，除本协议规定的之外，未赋予本协议各方其他权利。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">2、如本协议中的任何条款无论因何种原因完全或部分无效或不具有执行力，本协议的其余条款仍应有效并且有约束力。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt;">3、本协议中的标题仅为方便而设，在解释本协议时应被忽略。&nbsp;</p>', 1, 4, 1388970667, 1, 0, 1);
INSERT INTO `wit` (`id`, `name`, `content`, `project`, `user`, `time`, `selected`, `deleted`, `latest_version`) VALUES
(2, '智塔网企业用户网络服务使用协议', '<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">欢迎您使用智塔企业平台服务！</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">特别提示：</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">在使用智塔企业平台服务之前，您应当认真阅读并遵守《智塔企业会员服务协议》（以下简称&ldquo;本协议&rdquo;），请您务必审慎阅读、充分理解各条款内容，特别是免除或者限制责任的条款、争议解决和法律适用条款。免除或者限制责任的条款可能将以加粗字体显示，您应重点阅读。如您对协议有任何疑问的，应向智塔网咨询。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">当您按照注册页面提示填写信息、阅读并同意本协议且完成全部注册程序后，或您按照激活页面提示填写信息、阅读并同意本协议且完成全部激活程序后，或您以其他智塔允许的方式实际使用智塔企业平台服务时，即表示您已充分阅读、理解并接受本协议的全部内容，并与智塔企业平台达成协议。您承诺接受并遵守本协议的约定， 届时您不应以未阅读本协议的内容或者未获得智塔网对您问询的解答等理由，主张本协议无效，或要求撤销本协议。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">一、 协议范围</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">1、本协议由您与智塔企业平台的经营者共同缔结，本协议具有合同效力。</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="zh-CN" style="font-family: 宋体;">智塔企业平台的经营者是指法律认可的经营智塔企业平台网站的责任主体，智塔企业平台网站包括但不限于智塔网（域名为</span><span lang="en-US" style="font-family: Calibri;">witower</span><span lang="zh-CN" style="font-family: 宋体;">.</span><span lang="en-US" style="font-family: Calibri;">com</span><span lang="zh-CN" style="font-family: 宋体;">）项下的企业服务。有关智塔企业平台经营者的信息请查看智塔网公开公布的公司信息和证照信息。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、除另行明确声明外，智塔企业平台服务包含任何智塔网及其关联公司提供的基于互联网以及移动网的相关服务，且均受本协议约束。如果您不同意本协议的约定，您应立即停止注册/激活程序或停止使用智塔企业平台服务。</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="zh-CN" style="font-family: 宋体;">3、本协议内容包括协议正文、线下协议和</span><span lang="en-US" style="font-family: Calibri;">/</span><span lang="zh-CN" style="font-family: 宋体;">或合同及所有智塔网已经发布或将来可能发布的各类规则、公告或通知（以下合称&ldquo;智塔规则&rdquo;或&ldquo;规则&rdquo;）。所有规则为本协议不可分割的组成部分，与协议正文具有同等法律效力。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">4、智塔网有权根据需要不时地制订、修改本协议及/或各类规则，并以网站公示的方式进行变更公告，无需另行单独通知您。变更后的协议和规则一经在网站公布后，立即或在公告明确的特定时间自动生效。若您在前述变更公告后继续使用智塔企业平台服务的，即表示您已经阅读、理解并接受经修订的协议和规则。若您不同意相关变更，应当立即停止使用智塔企业平台服务。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">二、 注册与账户</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">1、主体资格</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">您确认，在您完成注册程序或以其他智塔允许的方式实际使用智塔企业平台服务时，<span style="font-weight: bold;">您应当是具备完全民事行为能权利能力和完全民事力的自然人、法人或其他组织。若您不具备前述主体资格，则您及您的监护人应承担因此而导致的一切后果，且智塔网有权注销或永久冻结您的账户，并向您及您的监护人索偿相应损失。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、注册和账户</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">当您按照注册页面提示填写信息、阅读并同意本协议且完成全部注册程序后，或在您按照激活页面提示填写信息、阅读并同意本协议且完成全部激活程序后，或您以其他智塔网允许的方式实际使用智塔企业平台服务时，您即受本协议约束。您可以使用您提供或确认的邮箱、用户名或者智塔网允许的其它方式作为登录手段进入智塔企业平台。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">您了解并同意，如您在智塔企业平台注册成功，您即可以同步获得智塔网登录名。您可以通过您的智塔网登录名直接享有智塔网以及后续可能开通的其他网站/服务（以下合称&ldquo;智塔网&rdquo;），而无需重新注册。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c)</span><span lang="zh-CN" style="font-family: 宋体;">您可以对账户设置智塔用户名，您也可以通过该智塔登录用户名密码登录智塔企业平台。您设置的用户名不得侵犯或涉嫌侵犯他人合法权益。如您设置的用户名涉嫌侵犯他人合法权益，智塔网有权终止向您提供智塔企业平台服务，注销您的相关用户名，及至相关账号。用户名被注销后将开放给任意用户注册。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">d)</span><span lang="zh-CN" style="font-family: 宋体;">您的登录用户名、注册邮箱和密码不得以任何方式买卖、转让、赠与或继承，除非有法律明确规定或司法裁定，并经智塔网同意，且需提供智塔网要求的合格的文件材料并根据智塔网制定的操作流程办理。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">3、用户信息</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">在注册或激活流程时，您应当依照法律法规要求，按相应页面的提示准确提供您的资料，并于资料信息变更时及时更新，以保证您所提交资料的真实、及时、完整和准确。</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">如有合理理由怀疑您提供的资料错误、不实、过时或不完整的，智塔网有权向您发出询问及/或要求改正的通知，并有权直接做出删除相应资料的处理，直至中止、终止对您提供部分或全部智塔企业平台服务。智塔网对此不承担任何责任，您将承担因此产生的任何直接或间接损失及不利后果。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">您应当准确填写并及时更新您提供的电子邮件地址、联系电话、联系地址、邮政编码等联系方式，以便智塔网或其他用户与您进行有效联系，因通过这些联系方式无法与您取得联系，导致您在使用智塔企业平台服务过程中产生任何损失或增加费用的，应由您完全独自承担。您了解并同意，您有义务保持你提供的联系方式的真实性和有效性，如有变更或需要更新的，您应按智塔网的要求进行相应操作。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">4、账户安全</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="zh-CN" style="font-family: 宋体;">您须自行负责对您的智塔网登录名、注册邮件和密码保密，且须对您在该登录名、注册邮件和密码下发生的所有活动（包括但不限于信息披露、发布创意需求项目、参与相关项目及其评分、网上点击同意或提交各类规则协议、网上续签协议或购买服务等）承担责任。您同意：(</span><span lang="en-US" style="font-family: Calibri;">a</span><span lang="zh-CN" style="font-family: 宋体;">) 如发现任何人未经授权使用您的智塔登录名、注册邮箱和密码，或发生违反保密规定的任何其他情况，您会立即通知智塔网，并授权智塔网进行相关处理；及 (</span><span lang="en-US" style="font-family: Calibri;">b</span><span lang="zh-CN" style="font-family: 宋体;">) 确保您在每个上网时段结束时，以正确步骤离开网站/服务。智塔网不能也不会对因您未能遵守本款规定而发生的任何损失负责。您理解智塔网对您的请求采取行动需要合理时间，智塔网对在采取行动前已经产生的后果（包括但不限于您的任何损失）不承担任何责任。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">5、智塔网登录名注销</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">如您连续三年未使用您的邮箱、用户名或智塔网认可的其他方式和密码登录过智塔企业平台，您的智塔网企业用户可能被注销，不能再登录智塔网及相关网站，所有智塔网站服务同时终止。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">您同意并授权智塔网，您如在智塔网及相关站有欺诈、发布的产品涉及假冒伪劣/侵权商品、侵犯他人合法权益或其他违反法律法规、智塔网站规则等行为，您的智塔网企业用户可能被注销、不能再登录智塔网及相关网站，所有智塔网站服务同时终止。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">三、 智塔企业平台服务</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">1、通过智塔网提供的智塔企业平台服务和其它服务，作为企业会员，您可在智塔企业平台上：</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">创建和维护产品信息</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">发布和维护创意项目需求信息</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c)</span><span lang="zh-CN" style="font-family: 宋体;">设定各个发布项目相应的悬赏额度</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">d)</span><span lang="zh-CN" style="font-family: 宋体;">对自身发布的创意项目进行日常管理（包括但不限于对版本进行日常评分和评价、对相应版本进行删除或修改等）</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">e)</span><span lang="zh-CN" style="font-family: 宋体;">在自身项目进入到打分期时对其项目进行评分并选定创意需求最终方案</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">具体以所开通的智塔企业平台提供的服务内容为准。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、<span style="font-weight: bold;">您在智塔企业平台上交易过程中与其他会员发生交易纠纷时，一旦您或其它会员任一方或双方共同提交智塔网要求调处，则智塔网作为独立第三方，有权根据单方判断做出调处决定，您了解并同意接受智塔的判断和调处决定。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">3、<span style="font-weight: bold;">您了解并同意，智塔网有权应政府部门（包括司法及行政部门）的要求，向其提供您在智塔网提供的用户信息和交易记录等必要信息。如您涉嫌侵犯他人知识产权等合法权益，则智塔网亦有权在初步判断涉嫌侵权行为存在的情况下，向权利人提供您必要的身份信息。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">4、您在使用智塔企业平台服务过程中，所产生的应纳税赋，以及一切硬件、软件、服务及其它方面的费用，均由您独自承担。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">四、智塔企业平台服务使用规范</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">1、在智塔企业平台上使用相关服务过程中，您承诺遵守以下约定：</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">a</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)实施的所有行为均遵守国家法律、法规等规范性文件及智塔企业平台各项规则的规定和要求，不违背社会公共利益或公共道德，不损害他人的合法权益，不偷逃应缴税费，不违反本协议及相关规则。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b</span><span lang="zh-CN" style="font-family: 宋体;">)在智塔网的创意悬赏交易过程中，遵守诚实信用原则，不采取不正当竞争行为，不扰乱网上交易的正常秩序，不从事与网上交易无关的行为。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c</span><span lang="zh-CN" style="font-family: 宋体;">)不发布国家禁止销售的或限制销售的商品或服务信息（除非取得合法且足够的许可），不发布涉嫌侵犯他人知识产权或其它合法权益的商品或服务信息，不发布违背社会公共利益或公共道德或智塔认为不适合在智塔企业平台上进行的商品或服务信息，不发布其它涉嫌违法或违反本协议及各类规则的信息。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">d</span><span lang="zh-CN" style="font-family: 宋体;">)不以虚构或歪曲事实的方式不当评价其他参与会员，不采取不正当方式制造或提高自身的知名度。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">e)&nbsp;</span><span lang="zh-CN" style="font-family: 宋体;">企业会员对自身发布的项目所属产品或项目及商标等拥有合法的品牌使用权及网络使用权，不得发布自身不拥有所有权或使用权的品牌或商品相关的项目。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">f</span><span lang="en-US" style="font-family: 宋体;">)</span><span lang="zh-CN" style="font-family: 宋体;">企业会员对自身发布的项目的最终方案拥有所有权，同时对选中的创意方案的所有参与版本都拥有其知识产权。但对没有选中的方案及其所有的任意版本都不享有相应的所有权。注：知识产权的转移在该项目的悬赏结束，相关积分计入参与的网络个人用户时发生。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">g</span><span lang="zh-CN" style="font-family: 宋体;">)不使用任何装置、软件或例行程序干预或试图干预智塔企业平台的正常运作或正在智塔企业平台上进行的任何交易、活动。您不得采取任何将导致不合理的庞大数据负载加诸智塔企业平台网络设备的行动。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、您了解并同意：</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">a</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)您如果违反前述承诺，产生任何法律后果的，您应以自己的名义独立承担所有的法律责任，并确保智塔网免于因此产生任何损失或增加费用。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">b</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)基于维护智塔企业平台正常秩序及安全的需要，智塔网有权在发生恶意扰乱正常秩序的情形下，执行关闭创意项目等操作。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">c</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)经国家行政或司法机关的生效法律文书确认您的相关项目或其他信息存在违法或侵权行为，或者智塔网根据自身的判断，认为您的行为涉嫌违反法律法规的规定或涉嫌违反本协议和/或规则的条款的，则智塔网有权在智塔企业平台上公示您的该等涉嫌违法或违约行为及智塔网已对您采取的措施。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">d</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)对于您在智塔企业平台上发布的涉嫌违法、涉嫌侵犯他人合法权利或违反本协议和/或规则的信息，智塔网有权不经通知您即予以删除或修改等，且按照规则的规定进行处罚。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">e</span><span lang="zh-CN" style="font-family: 宋体;">)如您涉嫌违反有关法律或者本协议之规定，使智塔网遭受任何损失，或受到任何第三方的索赔，或受到任何行政管理部门的处罚，您应当赔偿智塔网因此造成的损失和/或发生的费用，包括合理的律师费用。同时智塔网保留进一步追究相应的法律责任及追索赔偿的权利。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">五、责任范围和责任限制</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">1、智塔网负责按"现状"和"可得到"的状态向您提供智塔企业平台服务。但智塔网对智塔企业平台服务不作任何明示或暗示的保证，包括但不限于智塔企业平台服务的适用性、没有错误或疏漏、持续性、准确性、可靠性、适用于某一特定用途。同时，智塔网也不对智塔企业平台服务所涉及的技术及信息的有效性、准确性、正确性、可靠性、质量、稳定、完整和及时性作出任何承诺和保证。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">2、您了解智塔企业平台上的信息系用户自行发布，且可能存在风险和瑕疵。目前智塔网为会员提供一个信息交流平台，包括但不限于企业会员发布自身品牌的广告创意需求和个人会员提供创意方案文案的一个交易市场，本网站对交易双方在法律规定的范围内进行协助和管理。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold;">3</span><span lang="zh-CN" style="font-weight: bold;">、智塔网有权为参与任意项目的个人用户的创意版本进行评分，同时评分按照智塔网规定的计算方法，以一定的权重比例用于计算最终的悬赏分配；同时智塔网也有权利和义务对参与企业的项目的版本进行日常的管理和维护，对参与的版本进行必要的删除、修改等处理。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">4</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">、除非法律法规明确要求，或出现以下情况，否则，智塔网没有义务对所有用户的信息数据、商品和服务信息、悬赏交易行为以及与悬赏交易有关的其它事项进行事先审查。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">5</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">、您了解并同意，智塔网不对因下述任一情况而导致您的任何损害赔偿承担责任，包括但不限于利润、商誉、使用、数据等方面的损失或其它无形损失的损害赔偿 ：</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">a)使用或未能使用智塔企业平台服务。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">b)第三方未经批准的使用您的账户或更改您的数据。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">c)您对智塔企业平台服务的误解。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold;">d</span><span lang="zh-CN" style="font-weight: bold;">)任何非因智塔网的原因而引起的与智塔企业平台服务有关的其它损失。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">6</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">、不论在何种情况下，智塔网均不对由于信息网络正常的设备维护，信息网络连接故障，电脑、通讯或其他系统的故障，电力故障，罢工，劳动争议，暴乱，起义，骚乱，生产力或生产资料不足，火灾，洪水，风暴，爆炸，战争，政府行为，司法行政机关的命令或第三方的不作为而造成的不能服务或延迟服务承担责任。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">六、知识产权</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">1、智塔网及智塔网所使用的任何相关软件、程序、内容，包括但不限于作品、图片、档案、资料、网站构架、网站版面的安排、网页设计、经由本网站或广告商向会员呈现的广告或资讯，均由智塔网或其它权利人依法享有相应的知识产权，包括但不限于著作权、商标权、专利权或其它专属权利等，受到相关法律的保护。未经本网站或权利人明示授权，会员保证不修改、出租、出借、出售、散布本网站及本网站所使用的上述任何资料和资源，或根据上述资料和资源制作成任何种类产品。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、智塔网授予会员不可转移及非专属的使用权，使会员可以通过单机计算机使用本网站的目标代码（以下简称"软件"），但会员不得且不得允许任何第三方复制、修改、创作衍生作品、进行还原工程、反向组译，或以其它方式破译或试图破译源代码，或出售、转让"软件"或对"软件"进行再授权，或以其它方式移转"软件"之任何权利。会员同意不以任何方式修改"软件"，或使用修改后的"软件"。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">3、会员不得经由非智塔网所提供的界面使用本网站。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span lang="en-US">4</span><span lang="zh-CN">、会员通过智塔网提供的服务，参与在智塔网公开区域的任何内容，视为参与创作用户同意将参与的广告创意作品的知识产权部分划归发布企业会员所有。就相应的企业会员对其参与的项目创意提供的悬赏奖励作为知识产权的支付费用：</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">按照智塔网的规定，由企业会员、智塔网评分以及个人会员投票，来决定每个创意项目的奖金的分配比例；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">个人会员在评分及投票中没有获得悬赏奖励的，即使参与创作也视为将其参与创作的知识产权转移给相应的企业用户；个人会员</span><span lang="en-US" style="font-family: Calibri;">/</span><span lang="zh-CN" style="font-family: 宋体;">企业用户对获奖比例及名单等存在异议的，可以在网站内部的系统中进行投诉，由智塔网进行核实后确认，并给出最终解释和</span><span lang="en-US" style="font-family: 宋体;">/</span><span lang="zh-CN" style="font-family: 宋体;">或调整；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c)</span><span lang="zh-CN" style="font-family: 宋体;">智塔网拥有对此规定及相关比例等的解释权和修改权。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span lang="en-US">5</span><span lang="zh-CN">、获得相应知识产权的企业会员有权通过如下方式行使该部分权利，但是，会员声明保留该内容的著作权或该内容从其他网站转载而附带有原网站的著作权声明者除外：</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">通过复制、发行、信息网络传播、改编、翻译、汇编、摄制、出租、展览、表演、放映、广播等方式使用该内容；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">将该内容用于开发和运营各类电信增值业务及无线互联网业务（如短信、彩信、</span><span lang="en-US" style="font-family: 宋体;">WAP</span><span lang="zh-CN" style="font-family: 宋体;">、</span><span lang="en-US" style="font-family: 宋体;">IVR</span><span lang="zh-CN" style="font-family: 宋体;">、手机报、手机客户端等形式），对其中精彩的图文进行推广，推广方式包括智塔网站首页、子频道等链接推广或编辑出版等等；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c)</span><span lang="zh-CN" style="font-family: 宋体;">推广市场授权他人使用该内容，授权第三方标示来源于智塔网站；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">d)</span><span lang="zh-CN" style="font-family: 宋体;">对任何三方侵犯该内容的知识产权的行为独立起诉进行维权。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span lang="en-US">6</span><span lang="zh-CN">、个人用户参与部分的内容，若侵害到第三方的知识产权及相关合法权益，智塔网和</span><span lang="en-US">/</span><span lang="zh-CN">或企业用户不承担责任，并对因此侵权行为带来的损害，保留采取法律行动的权利。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span lang="en-US">7</span><span lang="zh-CN">、若您认为本网站刊载的内容侵犯您的知识产权（包括著作权、商标专用权等）、名誉权、肖像权等合法权益，请您与本网站联系，并向提交书面通知，要求删除涉嫌侵权内容。该通知书应当包括下列内容：</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">权利人的姓名（名称）、联系电话和通讯地址；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">要求删除的涉嫌侵权内容的具体名称及其对应的网络地址；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c)</span><span lang="zh-CN" style="font-family: 宋体;">构成侵权的初步证明材料，包括权利人身份证明或企业营业执照、权利权属证明等。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">七、协议终止</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">1、出现以下情况时，智塔网有权直接以注销账户的方式终止本协议，并有权永久冻结（注销）您的账户在智塔企业平台的权限和收回账户对应的用户名：</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">a</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)智塔网终止向您提供服务后，您涉嫌再一次直接或间接以他人名义注册为智塔用户的；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">b</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)您提供的电子邮箱不存在或无法接收电子邮件，且没有其他方式可以与您进行联系，或智塔网以其它联系方式通知您更改电子邮件信息，而您在智塔网通知后三个工作日内仍未更改为有效的电子邮箱的。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">c</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)您提供的用户信息中的主要内容不真实或不准确或不及时或不完整；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">d</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)本协议（含规则）变更时，您明示并通知智塔网不愿接受新的服务协议的；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-weight: bold; font-family: Calibri;">e</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">)其它智塔网认为应当终止服务的情况。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">2</span><span lang="zh-CN" style="font-family: 宋体;">、</span><span lang="zh-CN" style="font-weight: bold; font-family: 宋体;">您的账户服务被终止或者账户在智塔企业平台的权限被永久冻结（注销）后，</span><span lang="zh-CN" style="font-family: 宋体;">智塔网没有义务为您保留或向您披露您账户中的任何信息，也没有义务向您或第三方转发任何您未曾阅读或发送过的信息。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">3</span><span lang="zh-CN" style="font-family: 宋体;">、您同意，您与智塔网的合同关系终止后，智塔网仍享有下列权利：</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a</span><span lang="zh-CN" style="font-family: 宋体;">)继续保存您的用户信息及您使用智塔企业平台服务期间的所有交易信息。</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b</span><span lang="zh-CN" style="font-family: 宋体;">)您在使用智塔企业平台服务期间存在违法行为或违反本协议和/或规则的行为的，智塔网仍可依据本协议向您主张权利。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">5、智塔网中止或终止向您提供智塔企业平台服务后，对于您在服务中止或终止之前的交易行为依下列原则处理，您应独力处理并完全承担进行以下处理所产生的任何争议、损失或增加的任何费用，并应确保智塔网免于因此产生任何损失或承担任何费用：</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a</span><span lang="zh-CN" style="font-family: 宋体;">)您在服务中止或终止之前已经上传至淘宝平台的产品和项目信息，或项目尚未进入悬赏交易的，智塔网有权在中止或终止服务的同时删除此项产品或项目的相关信息；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b</span><span lang="zh-CN" style="font-family: 宋体;">)您在服务中止或终止之前发布悬赏交易，但合同尚未实际履行完成的，智塔网有权删除该创意需求信息及其产品或项目的相关信息；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c</span><span lang="zh-CN" style="font-family: 宋体;">)您在服务中止或终止之前，发布的悬赏需求进入到实际履行阶段（包括但不限于有用户参与、进入到打分期或投票阶段等），相当于与其他会员达成悬赏合同且已部分履行的，智塔网可以不删除该项项目，但智塔网有权在中止或终止服务的同时将相关情形通知您的交易对方。</span></p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">八、隐私权政策</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">1、信息使用：</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">智塔网不会向任何人出售或出借会员的个人或法人信息，除非事先得到会员得许可；</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">智塔网亦不允许任何第三方以任何手段收集、编辑、出售或者无偿传播会员的个人或法人信息。任何会员如从事上述活动，一经发现，智塔网有权立即终止与该会员的服务协议，查封其账号。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、信息披露：会员的个人或法人信息将在下述情况下部分或全部被披露：&nbsp;</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">经会员同意，向第三方披露；&nbsp;</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">若会员是合法的知识产权使用权人并提起投诉，应被投诉人要求，向被投诉人披露，以便双方处理可能的权利纠纷；&nbsp;</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">c)</span><span lang="zh-CN" style="font-family: 宋体;">根据法律的有关规定，或者行政、司法机关的要求，向第三方或者行政、司法机关披露</span><span lang="en-US" style="font-family: Calibri;">;</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">d)</span><span lang="zh-CN" style="font-family: 宋体;">若会员出现违反中国有关法律或者网站规定的情况，需要向第三方披露；&nbsp;</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">e)</span><span lang="zh-CN" style="font-family: 宋体;">为提供你所要求的产品和服务，而必须和第三方分享会员的个人或法人信息；&nbsp;</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">f)</span><span lang="zh-CN" style="font-family: 宋体;">其它智塔网根据法律或者网站规定认为合适的披露。&nbsp;</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">3、信息安全：&nbsp;</p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">a)</span><span lang="zh-CN" style="font-family: 宋体;">在使用智塔网服务进行网上交易时，请会员妥善保护自己的个人或法人信息，仅在必要的情形下向他人提供；&nbsp;</span></p>\n<p style="margin: 0in; font-family: tahoma, arial, ''Microsoft Yahei''; background-color: #f5f5f5; font-size: 9pt; color: #666666;"><span lang="en-US" style="font-family: Calibri;">b)</span><span lang="zh-CN" style="font-family: 宋体;">如果会员发现自己的个人或法人信息泄密，尤其是会员账户账户及密码发生泄露，请会员立即联络智塔网客服，以便我们采取相应措施。&nbsp;</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">智塔网将在智塔网相关平台公布并不时修订隐私权政策，隐私权政策构成本协议的有效组成部分。</p>\n<p style="margin: 0in; color: #333333; background-color: #f5f5f5; font-family: Calibri; font-size: 10.5pt;">&nbsp;</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;"><span style="font-weight: bold;">九、法律适用、管辖与其他</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">1、本协议之效力、解释、变更、执行与争议解决<span style="font-weight: bold;">均适用中华人民共和国大陆地区法律</span>，如无相关法律规定的，则应参照通用国际商业惯例和/或行业惯例。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">2、本协议包含了您使用智塔企业平台需遵守的一般性规范，同时您还需遵守适用于该平台的特殊性规范（具体请参见您与该平台签署的其他协议以及平台规则等内容）。一般性规范如与特殊性规范不一致或有冲突，则特殊性规范具有优先效力。</p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">3、因本协议产生之争议需根据您使用的服务归属的平台确定具体的争议对象，例如因您使用智塔网服务所产生的争议应由智塔网的经营者与您沟通并处理。<span style="font-weight: bold;">一旦产生争议，您与智塔网平台的经营者均同意以被告住所地人民法院为第一审管辖法院。</span></p>\n<p style="margin: 0in; background-color: #f5f5f5; font-family: 宋体; font-size: 9pt; color: #666666;">4、如本协议的中文版本和英文版本内容产生冲突，有其他语言版本，将以中文版本内容为准。本协议以其中文版本为最终生效版本。</p>\n<p>&nbsp;</p>', 2, 4, 1388973001, 1, 0, 2);
INSERT INTO `wit` (`id`, `name`, `content`, `project`, `user`, `time`, `selected`, `deleted`, `latest_version`) VALUES
(3, '智塔帮助 -- 项目常见问题', '<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">什么是创意？</span></strong></p>\n<p><span lang="EN-US" style="font-size: 10pt;">&nbsp;&nbsp; &nbsp;&nbsp;</span><span style="font-size: 10pt;">创意是网友为征集中的广告创意项目所设立的文案。一个项目可以有多个不同的文案，每个文案都独立作为一个创意。每个注册网友都可以随意参与到任何一个项目的文案的书写中。对于创意有以下几个限制：</span></p>\n<p style="text-indent: 20.1pt; mso-char-indent-count: 2.0;"><strong><span style="font-size: 10pt; font-family: 宋体;">每人只能最多发布一个新创意</span></strong></p>\n<p style="text-indent: 20.1pt; mso-char-indent-count: 2.0;"><strong><span style="font-size: 10pt; font-family: 宋体;">每个项目最多有五个创意</span></strong></p>\n<p style="text-indent: 20.1pt; mso-char-indent-count: 2.0;"><strong><span style="font-size: 10pt; font-family: 宋体;">每个创意是若干个版本共同组成的</span></strong></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">什么是版本？</span></strong></p>\n<p><span lang="EN-US" style="font-size: 10pt;">&nbsp;&nbsp; &nbsp;&nbsp;</span><span style="font-size: 10pt;">每个参与者的每次对创意的修改，都会保存成为一个版本。每个版本是追溯参与者贡献的重要因素。每个参与者的版本没有限制（我们鼓励大家参与，但是希望尽量减少版本的冗余数据）。</span></p>\n<p style="text-indent: 20.1pt; mso-char-indent-count: 2.0;"><strong><span style="font-size: 10pt; font-family: 宋体;">发布方和智塔会每天对进行中的项目版本进行审核和修订，并给出分数</span></strong></p>\n<p style="text-indent: 20.1pt; mso-char-indent-count: 2.0;"><strong><span style="font-size: 10pt; font-family: 宋体;">参与者的多个版本都可以被打分，在计算悬赏时分数会进行汇总至个人参与用户</span></strong></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">如何参加项目创意</span></strong></p>\n<p><span lang="EN-US" style="font-size: 10pt;">&nbsp; &nbsp; &nbsp;</span><span style="font-size: 10pt;">在智塔网参加项目创意很简单，只要您在项目页或首页找到想参与的品牌的广告需求项目，就可以使用编辑来用文字书写广告策划文案，来贡献自己的聪明才智了。</span></p>\n<p style="text-indent: 21.0pt;"><span style="font-size: 10pt;">目前还不能直接上传图片和视频（可以通过发布图片和视频的链接来呈现），我们会尽快完善这方面的功能，让智塔网的参与项目形式更加丰富多彩。</span></p>\n<p style="text-indent: 21.0pt;"><span style="font-size: 10pt;">由于大家可以共同为同一个创意贡献才智，希望参与者本着合作创新，求同存异的精神，为自己喜欢的品牌共同创造优秀的广告作品。</span></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">如何成为候选人</span></strong></p>\n<p class="MsoNormal" style="text-indent: 21.0pt;"><span style="font-size: 10pt; font-family: 宋体;">在您参与的项目中，发布方和智塔会尽量本着公正客观的立场为参与的版本进行打分，分数在<span lang="EN-US">1-10</span>分，分数越高表示对该版本越赞许。</span></p>\n<p class="MsoNormal" style="text-indent: 21.0pt;"><span style="font-size: 10pt; font-family: 宋体;">如果参与的这个创意在多个创意中被发布方选中，同时在这个方案中您发布的版本的分数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&gt;0</span><span style="font-size: 10pt; font-family: 宋体;">（即发布方或智塔给出的分数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&gt;0</span><span style="font-size: 10pt; font-family: 宋体;">），则您就会作为投票的候选人出现在投票列表中。</span></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">如何投票</span></strong></p>\n<p class="MsoNormal"><span lang="EN-US" style="font-size: 10pt; font-family: 宋体;">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 10pt; font-family: 宋体;">项目在打分期后，进入到三天的投票期。在投票期中，项目无法继续参与。同时在投票页会列示出候选人的名单。</span></p>\n<p class="MsoNormal"><span lang="EN-US" style="font-size: 10pt; font-family: 宋体;">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 10pt; font-family: 宋体;">每个人可以在每个项目的投票中，投上自己的（不多于）三票，来表达对候选人的支持。票数可以分别投给<span lang="EN-US">1-3</span>个候选人。</span></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">如何查看候选人贡献</span></strong></p>\n<p class="MsoNormal" style="text-indent: 20.0pt; mso-char-indent-count: 2.0;"><span style="font-size: 10pt; font-family: 宋体;">在投票页面的候选人列表中，每个候选人后面都有&ldquo;</span><span lang="EN-US" style="font-size: 12pt; font-family: Arial, sans-serif;">Ta</span><span style="font-size: 12pt; font-family: 华文行楷;">的贡献</span><span style="font-size: 10pt; font-family: 宋体;">&rdquo;链接，在这里你可以查询到该名候选人的在该中选的创意中的版本贡献，并可以和最终版本进行对比。</span></p>\n<p class="MsoNormal" style="text-indent: 21.0pt;"><span style="font-size: 10pt; font-family: 宋体;">由于创意的取舍及版本的打分，一定在某种程度上存在着主观的意识，我们很难完全做到让所有参与者都满意，我们会及时接受大家的反馈，虚心接受大家的建议和意见。</span><span style="font-size: 10pt; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;">同时也希望大家本着互谅合作的精神，在智塔这个创意平台中积极发挥自己的创意才智。</span></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">如何获得悬赏</span></strong></p>\n<p class="MsoNormal" style="text-indent: 21.0pt;"><span style="font-size: 10pt; font-family: 宋体;">在投票后，中选的候选人按照智塔网独特的、更加透明公正的悬赏分配比例计算方式计算积分，计入个人的积分帐户。</span></p>\n<p><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">悬赏计算方式</span></strong></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;</span><span style="font-size: 10pt;">智塔网设计了全新的悬赏分配方式，尽量做到公平公正和透明，并让所有用户都可以亲身参与到悬赏的分配中来。</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 1</span><span style="font-size: 10pt;">）发布方：在创意进行阶段和打分期，为参与者的版本打分数（</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1-10</span><span style="font-size: 10pt;">），分数越高代表对参与者的创意越满意；</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2</span><span style="font-size: 10pt;">）发布方：在创意结束后的打分期中（结束后的</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1</span><span style="font-size: 10pt;">天，在打分期中，创意是关闭的，无法继续参与），在多个创意方案中选定一个方案；</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 3</span><span style="font-size: 10pt;">）智塔网：在创意进行阶段和打分期，为参与者的版本打分数（</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1-10</span><span style="font-size: 10pt;">）；</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 4</span><span style="font-size: 10pt;">）您参与的这个方案被发布方选中，同时在这个方案中您发布的版本的分数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&gt;0</span><span style="font-size: 10pt;">（即发布方或智塔给出的分数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&gt;0</span><span style="font-size: 10pt;">），则您就会作为投票的候选人出现在投票列表中，最后的奖金分配必然有您的一杯羹；</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 5</span><span style="font-size: 10pt;">）方案在打分期后，进入到</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">3</span><span style="font-size: 10pt;">天的投票期。在投票期中，所有的注册用户都可以为心仪的候选人投上自己的票数（不多于</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">3</span><span style="font-size: 10pt;">票），来表示自己对候选人的支持；</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 6</span><span style="font-size: 10pt;">）投票结束后，进行最后的悬赏分配，按照</span><span style="font-size: 10pt;">注册用户投票</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">/</span><span style="font-size: 10pt;">发布方打分</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">/</span><span style="font-size: 10pt;">智塔打分</span><span style="font-size: 10pt;">比例为</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;"> 2</span><span style="font-size: 10pt;">：</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">2</span><span style="font-size: 10pt;">：</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1</span><span style="font-size: 10pt;">来计算最后的悬赏积分。例如：候选人</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">A</span><span style="font-size: 10pt;">在总数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">100</span><span style="font-size: 10pt;">票的注册用户投票中获得了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">30</span><span style="font-size: 10pt;">票；在企业的中选创意打分中，获得了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">20</span><span style="font-size: 10pt;">分（企业在该创意中总共给出了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">50</span><span style="font-size: 10pt;">分至数名候选人）；在智塔的该中选创意的打分中，获得了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">10</span><span style="font-size: 10pt;">分（智塔在该创意中总共给出了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">50</span><span style="font-size: 10pt;">分至数名候选人）。而该创意悬赏方案的悬赏金额为</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">5000</span><span style="font-size: 10pt;">元，则</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">A</span><span style="font-size: 10pt;">获得的悬赏积分计算如下：</span></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;">&nbsp;</p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span style="font-size: 10pt;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; [(30/100)X2+(20/50)X2+(10/50)X1]/(2+2+1)X5000=1600元</span></p>\n<h4 style="margin: 0cm; margin-bottom: .0001pt;"><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">&nbsp;</span></strong></h4>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><strong><span style="font-size: 14.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; font-weight: normal; mso-bidi-font-weight: bold;">如何申请悬赏</span></strong></p>\n<p class="MsoNormal" style="text-indent: 20.0pt; mso-char-indent-count: 2.0;"><span style="font-size: 10.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-ascii-theme-font: minor-latin; mso-fareast-font-family: 宋体; mso-fareast-theme-font: minor-fareast; mso-hansi-font-family: Calibri; mso-hansi-theme-font: minor-latin;">在投票结束后，悬赏积分计入到候选人的个人积分帐户中之后，您可以随时在&ldquo;积分&rdquo;页面查看目前您的积分额度，只要大于</span><span lang="EN-US" style="font-size: 10.0pt;">0</span><span style="font-size: 10.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-ascii-theme-font: minor-latin; mso-fareast-font-family: 宋体; mso-fareast-theme-font: minor-fareast; mso-hansi-font-family: Calibri; mso-hansi-theme-font: minor-latin;">，您就可以在页面的&ldquo;申请提现&rdquo;中，输入您要提取的金额并选择申请提现。</span></p>\n<p class="MsoNormal" style="text-indent: 20.0pt; mso-char-indent-count: 2.0;"><span style="font-size: 10.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-ascii-theme-font: minor-latin; mso-fareast-font-family: 宋体; mso-fareast-theme-font: minor-fareast; mso-hansi-font-family: Calibri; mso-hansi-theme-font: minor-latin;">智塔在收到您的提现申请后，会在</span><span lang="EN-US" style="font-size: 10.0pt;">1-3</span><span style="font-size: 10.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-ascii-theme-font: minor-latin; mso-fareast-font-family: 宋体; mso-fareast-theme-font: minor-fareast; mso-hansi-font-family: Calibri; mso-hansi-theme-font: minor-latin;">个工作日内将您申请的额度按照您个人帐户信息，汇至您指定的银行中。</span></p>\n<p><span style="font-size: 10.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-ascii-theme-font: minor-latin; mso-fareast-theme-font: minor-fareast; mso-hansi-font-family: Calibri; mso-hansi-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA;">我们也会尽快的开通其他的电子支付方式，如支付宝，快钱等，来简化和方便您的提现。</span></p>', 3, 4, 1388995739, 1, 0, 3),
(4, '关于智塔网', '<p>&nbsp;</p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">智塔网是做什么的</span></strong><strong><span style="font-size: 14pt; font-family: 华文行楷;">：</span></strong><span style="font-size: 10pt;">智塔网是一家探索全新品牌广告推广商业模式的网站，在此我们提供一个自由开放的威客悬赏平台，由网民自主合作创作品牌广告和/或推广文案，并根据创意贡献和注册网友投票比例，来确定获得企业发布方提供的悬赏额度。</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">(</span><span style="font-size: 10pt;">如何确定贡献</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">?</span><span style="font-size: 10pt;">请参看</span><span style="font-size: 10pt;">悬赏分配方式<span lang="EN-US">)</span></span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">智塔网和其他的威客网站有什么区别：</span></strong></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt;">&nbsp; &nbsp; &nbsp;1</span><span style="font-size: 10pt;">）在智塔网，我们先期主要提供品牌广告文案的悬赏参与。这意味着参与者无需多么专业的知识，也无需花巨大的精力在设计或者开发上，只要您有</span><span style="font-size: 12pt; font-family: 华文行楷; color: red;">参与创作的热情</span><span style="font-size: 12pt; font-family: 宋体;">，</span><span style="font-size: 12pt; font-family: 华文行楷; color: red;">善于观察生活的心</span><span style="font-size: 10pt; font-family: 宋体;">，</span><span style="font-size: 10pt;">再加上</span><span style="font-size: 10pt;"><span style="font-size: 10.5pt; font-family: 华文行楷; color: red;">一点奇思妙想</span>以及</span><span style="font-size: 10.5pt; font-family: 华文行楷; color: red;">乐于和他人分享合作的精神</span><span style="font-size: 10pt;">。</span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt;">&nbsp; &nbsp; &nbsp;2</span><span style="font-size: 10pt;">）参与方式：在智塔网，我们的参与形式是合作创作，而不同于传统的威客形式。作为参与者，您可以在其他人的创意基础上进行完善，寻找合作创作的乐趣；也可以通过首先&ldquo;发布创意&rdquo;，来最早贡献您的创意开局，让其他人在您的创意基础上继续发挥。一个小小的思想的火花可能就会为一个品牌带来让人终身难忘的印记，而您就可能是他的创造者之一。</span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt;">&nbsp; &nbsp; &nbsp;3</span><span style="font-size: 10pt;">）悬赏分配方式：传统的威客网站多采用投标制，一人或多人分等级中奖。而决定中奖人的为发标者。智塔网独创的分配计算方式，由悬赏发布方和智塔合作确定候选人，而候选人接受所有注册用户的投票来决定最终的悬赏分配比例。每一个候选人的贡献都是透明的，做到尽量让每一步都可以透明公开，并可以让所有的个人用户可以能够参与其中，加入到分配的计算中来。（具体的悬赏分配方式见下述的&ldquo;全新的悬赏分配方式&rdquo;）</span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">我们的理念<span lang="EN-US">:</span></span></strong><strong><span lang="EN-US" style="font-family: 宋体;">&nbsp;</span></strong><span style="font-size: 10.5pt; font-family: 华文行楷; color: red;">&ldquo;</span><span style="font-size: 10.5pt; font-family: 华文行楷; color: red;">不做总统，就做广告人！&rdquo;</span><span style="font-size: 10pt;">罗斯福总统的这句名言曾让无数广告人心潮澎湃。广告人，可以天马行空；广告人，可以异想天开。让自己的创意在短短的几十秒内尽情展现。智塔网的理念就是打造一个让每个人都能亲身参与到广告创作及其推广当中的平台：在这里，您无需是专业的广告人士，只需要有一颗热情创作的心。</span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">为什么我要参与<span lang="EN-US">:</span></span></strong><strong><span lang="EN-US" style="font-size: 14pt; font-family: 华文行楷;"><br /></span></strong><strong><span lang="EN-US" style="font-size: 14pt; font-family: 宋体;">&nbsp;</span></strong><strong><span lang="EN-US" style="font-size: 14pt; font-family: 华文行楷;">&nbsp;&nbsp;</span></strong><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">物质奖励</span></strong></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt;">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style="font-size: 10pt;">广告创意的需求发布方（通常为一个<span lang="EN-US">/</span>多个品牌的企业所有者）会在智塔网，以悬赏的方式来进行广告创意的征集。参与者只要有份参与的创意被选中，就有机会成为候选人，候选人会按照三方（发布方，注册用户和智塔网）的评分和投票来决定奖金的分配比例。投票结束后，悬赏会按照计算比例自动打入对应的参与者的积分帐户。（候选人获得的悬赏积分会在帐户的积分中查询到，并可以随时兑换成等值货币。）</span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><strong><span lang="EN-US" style="font-size: 14pt; font-family: 宋体; font-weight: normal;">&nbsp; </span></strong><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">精神追求<span lang="EN-US">:</span></span></strong></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; 1</span><span style="font-size: 10pt;">）发挥自己的创意，让广告变得更有创造性和乐趣，让那些千篇一律、甚至令人厌恶的广告见鬼去吧</span><span style="font-size: 10pt;">。</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;"><br /></span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; 2</span><span style="font-size: 10pt;">）</span><span style="font-size: 10pt;">在多媒体看到自己参与创作的作品</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">,</span><span style="font-size: 10pt;">是件很爽的事情。</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;"><br /></span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; 3</span><span style="font-size: 10pt;">）与他人合作，贡献并分享你的创意</span><span style="font-size: 10pt;">。</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;"><br /></span><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">我该如何参与<span lang="EN-US">:</span></span></strong><strong><span lang="EN-US" style="font-size: 14pt; font-family: 华文行楷;"><br /></span></strong><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; 1</span><span style="font-size: 10pt;">）</span><span style="font-size: 10pt;">参与智塔网的广告创意文案非常简单，只要您在首页或者项目页寻找到您乐于参与的关于品牌或产品的创意文案需求，作为注册用户，就可以在页面中找到&ldquo;编辑&rdquo;或者&ldquo;发布创意&rdquo;，然后就可以用文字与他人一同分享您的独特创意文案了。您可以在其他人的创意基础上进行完善，寻找合作创作的乐趣；也可以通过&ldquo;发布创意&rdquo;，来首先贡献您的创意，让其他人在您的创意基础上继续发挥。</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;"><br /></span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; 2</span><span style="font-size: 10pt;">）</span><span style="font-size: 10pt;">除了参与创意，在项目进入投票期的三天里，您可以为进入投票候选人名单的优秀创意贡献者投票（每人限定</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">3</span><span style="font-size: 10pt;">票），表达对其突出贡献的支持。</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;"><strong><span style="font-size: 10.5pt; font-family: 华文行楷;">您的支持可以改变悬赏分配的比例哦！</span></strong><br />&nbsp; &nbsp; &nbsp; &nbsp; 3</span><span style="font-size: 10pt;">）</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">SNS</span><span style="font-size: 10pt;">的交流与分享：在其他</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">SNS</span><span style="font-size: 10pt;">渠道来与朋友们分享您的创意。我们会尽快地开通更多的</span><span lang="EN-US" style="font-size: 10pt; font-family: Calibri, sans-serif;">SNS</span><span style="font-size: 10pt;">接口，让您更轻松的和朋友分享您的创意。</span></p>\n<p style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><strong><span style="font-size: 14pt; font-family: 华文行楷; font-weight: normal;">全新的悬赏分配方式<span lang="EN-US">:</span></span></strong></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;</span><span style="font-size: 10pt;">智塔网设计了全新的悬赏分配方式，尽量做到公平公正和透明，并让所有用户都可以亲身参与到悬赏的分配中来。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;1</span><span style="font-size: 10pt;">）发布方：在创意进行阶段和打分期，为参与者的版本打分数（</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1-10</span><span style="font-size: 10pt;">），分数越高代表对参与者的创意越满意；（没有打分数，则代表打分方认为该版本并没有特别的贡献）</span></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;2</span><span style="font-size: 10pt;">）发布方：在创意结束后的打分期中（结束后的</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1</span><span style="font-size: 10pt;">天，在打分期中，创意是关闭的，无法继续参与），在多个创意方案中选定一个方案；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;3</span><span style="font-size: 10pt;">）智塔网：在创意进行阶段和打分期，为参与者的版本打分数（</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1-10</span><span style="font-size: 10pt;">）；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;4</span><span style="font-size: 10pt;">）您参与的这个创意方案被发布方选中，同时在这个方案中您发布的版本的分数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&gt;0</span><span style="font-size: 10pt;">（即发布方或智塔给出的分数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&gt;0</span><span style="font-size: 10pt;">），则您就会作为投票的候选人出现在投票列表中，最后的奖金分配必然有您的一杯羹；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;5</span><span style="font-size: 10pt;">）方案在打分期后，进入到</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">3</span><span style="font-size: 10pt;">天的投票期。在投票期中，所有的注册用户都可以为心仪的候选人投上自己的票数（不多于</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">3</span><span style="font-size: 10pt;">票），来表示自己对候选人的支持；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp;6</span><span style="font-size: 10pt;">）投票结束后，进行最后的悬赏分配，按照</span><span style="font-size: 10pt;">注册用户投票</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">/</span><span style="font-size: 10pt;">发布方打分</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">/</span><span style="font-size: 10pt;">智塔打分</span><span style="font-size: 10pt;">比例为</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp;2</span><span style="font-size: 10pt;">：</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">2</span><span style="font-size: 10pt;">：</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">1</span><span style="font-size: 10pt;">来计算最后的悬赏积分。例如：候选人</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">A</span><span style="font-size: 10pt;">在总数</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">100</span><span style="font-size: 10pt;">票的注册用户投票中获得了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">30</span><span style="font-size: 10pt;">票；在企业的中选创意打分中，获得了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">20</span><span style="font-size: 10pt;">分（企业在该创意中总共给出了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">50</span><span style="font-size: 10pt;">分至数名候选人）；在智塔的该中选创意的打分中，获得了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">10</span><span style="font-size: 10pt;">分（智塔在该创意中总共给出了</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">50</span><span style="font-size: 10pt;">分至数名候选人）。而该创意悬赏方案的悬赏金额为</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">5000</span><span style="font-size: 10pt;">元，则</span><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">A</span><span style="font-size: 10pt;">获得的悬赏积分计算如下：</span></p>\n<p class="MsoNormal" style="margin: 0px 0px 10px; color: #333333; font-family: tahoma, arial, ''Microsoft Yahei''; font-size: 13px; line-height: 19px; background-color: #f5f5f5;"><span lang="EN-US" style="font-size: 10pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; [(30/100)X2+(20/50)X2+(10/50)X1]/(2+2+1)X5000=1600元</span></p>\n<p>&nbsp;</p>', 4, 4, 1392016930, 1, 0, 4);
INSERT INTO `wit` (`id`, `name`, `content`, `project`, `user`, `time`, `selected`, `deleted`, `latest_version`) VALUES
(5, 'TELL ME WHY?', '<p><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">智塔平台能为合作企业做什么</span></strong></p>\n<p class="MsoNormal" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri; mso-bidi-font-weight: bold;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-weight: bold;">品牌共创，让您的品牌更加深入人心：智塔网是一个探索新型互动广告的网站平台。在这里，我们借由互联网的广阔平台，提供一个通过互动广告文案及其他方式，让企业能够在品牌创立及传播中与广大的用户（及潜在用户）进行有效的互动沟通</span><span lang="EN-US" style="font-size: 9.0pt; mso-bidi-font-weight: bold;">--</span><span style="font-size: 12.0pt; font-family: 华文行楷; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体; color: red; mso-font-kerning: 0pt;">&ldquo;亲身参与的，才是最好的&rdquo;</span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-weight: bold;">，进而让消费者融入到品牌建设中来，成为您的品牌建设中最有力的一环，让&ldquo;我的品牌&rdquo;成为&ldquo;我们的品牌&rdquo;。</span></p>\n<p class="MsoNormal" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri; mso-bidi-font-weight: bold;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; &nbsp;</span></span><!--[endif]--><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-weight: bold;">创造更优秀的广告及相关作品，让人们对品牌拥有更加深刻的记忆。利用互联网用户无穷的想象力</span><span style="font-size: 9.0pt; font-family: 宋体; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt;">来丰富品牌的广告形象，改变千篇一律的广告内容，让精彩独特的广告来助力您的品牌企业。</span></p>\n<p class="MsoNormal" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri; mso-bidi-font-weight: bold;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; &nbsp;</span></span><!--[endif]--><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-weight: bold;">参与者更乐于对自身参与的广告及相关作品进行心甘情愿的传播与分享，让自媒体浪潮对企业的品牌宣传起到推波助澜的效果。</span></p>\n<p class="MsoNormal" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri; mso-bidi-font-weight: bold;">4.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-weight: bold;">培养潜在用户及开发更多的产品需求：通过多种互动广告方式让更多的参与者成为潜在用户，并认识和了解品牌；同时</span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">用户或潜在用户通过广告创意的互动，可以从某种角度让发布企业更加了解其内在的更多的消费需求，从而帮助企业发掘更多的产品以及周边。</span></p>\n<p class="MsoNormal" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri; mso-bidi-font-weight: bold;">5.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; &nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-weight: bold;">企业文化延伸：一个品牌的广告，承载着其拥有者对自身品牌的利益认知和情感归属，而让更多的人参与到品牌建设中，会让其在实现自我的同时，更加的理解和认可品牌的价值观，个性，品味等；同时积极的参与全新的商业模式也有助于树立企业</span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">勇于创新和尝试的形象，为企业的无形资产增加重要的砝码。</span></p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">为什么广告文案创意的征集要在智塔网进行</span></strong></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -18.0pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">多视角：普通的广告一般都是由少数的广告业内人士创意，这些人生活圈子单一而重叠，策划的品牌广告很难能够在深层次打动广大的消费者，因此更多的是以大规模的推广渠道来&ldquo;霸占&rdquo;消费者的视线。这种方法的效果更多的是建立在企业方的资金推动上。而智塔网提供的互动广告文案方式，是让更多的人能够以参与广告文案的方式来了解并宣传品牌，其发掘的独特视角能够引起更多人的共鸣，同时让更多的人牢记企业的品牌及形象。同时本身文案的悬赏，对于企业品牌来说就是一次良好的宣传推广。</span></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">高频率并应时：企业可以针对不同的时期及变化，随时发布创意需求；同时参与者也会根据不同的时令（如各种节日或发生的令人关注的事件等）以及当时的流行（包括语言、趋势等）为需求方进行当令应时的创作，让创作出的推广作品更加的贴合时代，更容易让广大的消费者铭记。</span></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><span lang="EN-US" style="text-indent: -18pt; font-size: 9pt;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="text-indent: -18pt; font-size: 9pt; font-family: 宋体;">互动性：智塔网的创作形式让更多的人参与到了广告的创作当中，有了互动性，才行让品牌更加的深入人心。一个让人印象深刻的广告必然是有参与感的，或反之亦然。</span></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;">4.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; &nbsp;</span></span><!--[endif]--><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">企业形象：全新的商业模式有助于帮助树立企业勇于创新和尝试的形象，为企业的无形资产增加重要的砝码。</span></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;">5.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">了解客户与潜在客户：用户或潜在用户通过广告创意的互动，可以让企业更加了解其内在的更多的消费需求，从而帮助企业发掘更多的产品以及周边。</span></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -18.0pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;">&nbsp;6.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;">低成本，多渠道：企业在智塔网发布的创意文案需求，所需的悬赏金额可控而且金额数量无需巨大，一定数量的悬赏就可以吸引不少的用户参与。同时作品做成多媒体作品后，企业也可以更加多的尝试目前新兴的自媒体中进行推广，比如微博、微信等，这些渠道推广费用较低且可控，同时参与者的成就感也会让其在自身能力范围内的自媒体内，协助推广其参与的企业品牌广告。当然如果企业能力允许，在一些较高费用但是受众更广的渠道（如卫视等）发布，同时创造一些讨论话题的方式，也不失为一个快速推广品牌的好方法。</span></p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">企业如何操作</span></strong></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;"><span style="font-family: Verdana, Arial, Helvetica, sans-serif; text-indent: -24px;">1.&nbsp;</span><span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><!--[endif]--><span style="font-size: 9pt;">品牌授权：发布创意悬赏的企业应该合法拥有其所持的品牌，同时授权在智塔网上进行使用。</span></p>\n<p class="MsoListParagraph" style="margin-left: 42.55pt; text-indent: -16.3pt; mso-char-indent-count: 0; mso-list: l0 level1 lfo1;"><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;"><span style="font-family: Verdana, Arial, Helvetica, sans-serif;">2</span>.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9pt;">日常管理：合作企业方在智塔网拥有独立的企业管理帐号，可以进行如下的日常管理行为，而智塔网将尽力协助企业管理并给予必要和大力的支持：</span></p>\n<p class="MsoListParagraph" style="margin-left: 63.0pt; text-indent: -18.75pt; mso-char-indent-count: 0; mso-list: l0 level2 lfo1;"><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;"><span style="font-family: Calibri, sans-serif; font-size: 9pt;">a</span>.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; &nbsp;&nbsp;</span></span><!--[endif]--><span style="font-size: 9pt;">发布项目：按照企业需要，随时在智塔网的平台上发布所需的广告创意需求项目，征集创意文案。在发布项目中，企业根据自身需要，设定悬赏金额（用于悬赏的积分由企业在智塔网存入的货币来决定，具体额度由双方的具体合同来规定）、悬赏期限（一般设定为</span><span lang="EN-US" style="font-size: 9.0pt;">7-15</span><span style="font-size: 9pt;">天）、以及所要征集的广告方案的要求（如节日相关、校园相关、特定人群等）。</span></p>\n<p class="MsoListParagraph" style="margin-left: 63.0pt; text-indent: -18.75pt; mso-char-indent-count: 0; mso-list: l0 level2 lfo1;"><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;"><span style="font-family: Calibri, sans-serif; font-size: 9pt;">b</span>.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; &nbsp;&nbsp;</span></span><!--[endif]--><span style="font-size: 9pt;">管理项目：在项目进行期中，对项目进行必要的修订与管理。</span></p>\n<p class="MsoListParagraph" style="margin-left: 63.0pt; text-indent: -18.75pt; mso-char-indent-count: 0; mso-list: l0 level2 lfo1;"><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;"><span style="font-family: Calibri, sans-serif; font-size: 9pt;">c</span>.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9pt;">日常评分：为创意进行中的创意版本评分，对于好的创意版本给予一定的评分（</span><span lang="EN-US" style="font-size: 9.0pt;">1-10</span><span style="font-size: 9pt;">分，分数越高意味着企业越欣赏该名参与者的版本；当然，对于几乎没有贡献的版本，可以不给予分数），该分数将会按照比例来计算参与者的贡献，并分配悬赏金额。</span></p>\n<p class="MsoListParagraph" style="margin-left: 63.0pt; text-indent: -18.75pt; mso-char-indent-count: 0; mso-list: l0 level2 lfo1;"><span lang="EN-US" style="font-size: 9.0pt; mso-fareast-font-family: Calibri; mso-bidi-font-family: Calibri;"><span style="font-family: Calibri, sans-serif; font-size: 9pt;">d</span>.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9pt;">选定方案：在悬赏结束后的一天内，企业可以自由在几个方案中选定心仪的一个作为最终中选方案，那么意味着中选方案中的，得到企业（和</span><span lang="EN-US" style="font-size: 9.0pt;">/</span><span style="font-size: 9pt;">或智塔）评分大于零的参与者就会作为候选人出现在最后参与分配奖金的名单中。如果企业没有正式的选择一个创意作为最终项目方案，那么系统会按照之前企业用户和智塔网为用户所打的分数，自动选择版本总分数最高的那个方案作为最终方案。</span></p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">联系我们</span></strong></p>\n<p class="MsoNormal" style="margin-left: 28.25pt; mso-para-margin-left: 2.69gd;"><span style="font-size: 9pt;">智塔网愿竭诚为您服务，如果您对智塔网有兴趣或有任何疑问，请按照如下的方式与我们联系：</span></p>\n<p class="MsoNormal" style="margin-left: 28.25pt; mso-para-margin-left: 2.69gd;"><span lang="EN-US" style="font-size: 9.0pt; mso-bidi-font-weight: bold;">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style="font-size: 9pt;">联系电话：</span><span lang="EN-US" style="font-size: 9pt; font-family: Calibri, sans-serif;">13661408983</span></p>\n<p class="MsoNormal" style="margin-left: 28.25pt; mso-para-margin-left: 2.69gd;"><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: 宋体; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA; mso-bidi-font-weight: bold;">&nbsp; &nbsp; &nbsp; &nbsp; E-mail:Contactus@witower.com</span></p>\n<p style="margin: 0in; font-family: 宋体; font-size: 10.5pt;">&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>', 5, 4, 1392018983, 1, 0, 5);
INSERT INTO `wit` (`id`, `name`, `content`, `project`, `user`, `time`, `selected`, `deleted`, `latest_version`) VALUES
(6, ' KNOW HOW!', '<p><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">企业如何申请智塔网企业用户及通过认证：</span></strong>&nbsp;</p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">在智塔网首页的页面，您可以找到&ldquo;注册&rdquo;，在注册页面中选择&ldquo;注册为企业用户&rdquo;并选择输入一定的公司简介，并&ldquo;同意&lsquo;智塔网企业用户协议&rsquo;&rdquo;。智塔网会在进行必要的审核和核实后，为您的用户升级成为企业用户。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">您也可以通过&ldquo;联系我们&rdquo;中的联系方式和智塔网取得联系，由客服人员为您提供必要的指导和帮助，并直接注册成为企业用户。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">注册成为企业会员后，使用企业用户账号登录后，可以在页面上方找到&ldquo;管理&rdquo;的链接，点击后就可以进入到企业后台的管理页面，用于发布创意项目。</span></p>\n<p><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">如何获得悬赏积分用于发布项目<span lang="EN-US">:</span></span></strong></p>\n<p style="margin: 0cm; margin-bottom: .0001pt; text-indent: 22.5pt; mso-char-indent-count: 2.5;"><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&nbsp;&nbsp;</span><span lang="EN-US" style="font-size: 10.0pt;"><span style="font-size: 9pt; text-indent: 22.5pt;">注册的企业用户，可以用于悬赏的积分，由企业在智塔网存入的货币来决定，具体额度由双方的具体合同来规定。</span></span></p>\n<p><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">为什么要维护产品信息</span></strong></p>\n<p style="margin-top: 0cm; margin-right: 0cm; margin-bottom: .0001pt; margin-left: 1.0cm; mso-para-margin-top: 0cm; mso-para-margin-right: 0cm; mso-para-margin-bottom: .0001pt; mso-para-margin-left: 2.7gd;"><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">在智塔网发布项目，需要先维护产品信息，然后在产品的项下来发布创意项目。这样的设置有利于企业对于项目的管理；也让参与创意的用户可以通过产品及相关介绍更加了解其参与的项目，更有利于创作出贴近品牌产品的作品。</span>&nbsp;</p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">如何发布和维护项目</span></strong></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">发布新项目：在企业用户的后台管理页面的左侧工具栏中，您可以找到&ldquo;项目管理&rdquo;，点击后，就可以&ldquo;增加新项目&rdquo;：</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">a.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">项目名称：用于显示在首页及其他页面上的，关于该创意项目的简要描述。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">b.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">所属产品：该创意属于哪个产品，产品的信息会显示在项目创意的下方，让参与者更加的了解项目的及产品的信息。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">c.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">项目图片：项目所配发的相关图片。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">d.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">项目描述：对项目的要求描述，如&ldquo;该项目征集视频</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">30</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">秒左右的广告创意，要求与情人节相关，温馨浪漫或有趣搞笑，目标群体为上海地区的高校学生，可以使用上海话&rdquo;。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">e.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">创意征集：为征集的创意的起始和结束日期：默认的起始日期为当前日期</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">+1</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">天，即目前发布的项目进入准备期；当然发布的企业用户可以将起始日期更改为当前日期，即该项目马上进入到创意的征集中。结束日期为开始日期之后的</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">7-15</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">天的范围内，具体日期由企业用户自行设定。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">f.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">投票评选：投票的起始和结束日期：该起始日期由系统自动按照创意征集的结束日期进行计算，一般为创意征集的结束日期</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">+2</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">天。在创意征集和投票评选之间的一天为打分期及选定创意方案期，即这一天企业需要为创意的多种方案中选择一个最心仪的方案用于最后的投票评定；如果企业没有选择，则系统会按照日常的版本打分中，自动选择版本总分数最高的一个方案作为最终的入选方案；投票结束日期为投票起始日期</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">+3</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">天。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">g.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">悬赏金额：用于悬赏的金额。该金额上限由企业用户的悬赏积分来决定；在该范围内，企业可以自由决定每个项目要使用的悬赏积分；在企业发布项目后，该部分的悬赏金额作为&ldquo;已冻结积分&rdquo;，在项目结束（即投票结束，积分记入相关的参与用户帐户）后，从企业用户的积分帐户中扣除。悬赏积分的使用情况，可以在企业的积分帐户中进行查询。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 60.55pt; text-indent: -18pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">h.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">关键词：企业用户可以设定若干关键词用于参与者快速的搜索，以便更好的找到发布的相关项目。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">维护项目：企业用户可以在项目进行时随时修改一些必要的信息，如标题，内容等。至于悬赏金额和悬赏期限等内容在企业发布后无法自行更改，如果确实需要进行更正，则企业用户可以联系智塔网的客服人员，由其在核实后进行必要的更正。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><span lang="EN-US" style="text-align: justify; text-indent: -11.05pt; font-size: 9pt; font-family: Calibri, sans-serif;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="text-align: justify; text-indent: -11.05pt; font-size: 9pt;">在项目进行中，企业可以对参与的内容进行一些必要的修正和管理，对出现一些不合企业要求的内容，如不合法的言论（涉及政治，色情等的言论）或</span><span lang="EN-US" style="text-align: justify; text-indent: -11.05pt; font-size: 9pt; font-family: Calibri, sans-serif;">/</span><span style="text-align: justify; text-indent: -11.05pt; font-size: 9pt;">和对企业不利及不当的内容时，企业可以通过编辑方案来更正，同时可以向智塔相关人员投诉，对发布者进行相应的处理。</span><span style="font-family: Calibri, sans-serif; font-size: 10.5pt;">&nbsp;</span></p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">如何查看创意</span></strong></p>\n<p>&nbsp;<span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">查看参与者的创意版本，可以通过如下几个方法：</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">1. &nbsp;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">创意项目页面，每个创意方案的&ldquo;编辑&rdquo;功能后面，都有&ldquo;版本&rdquo;链接，点击后，就可以在页面中查看每个参与用户对该创意方案的版本贡献；点击每个版本，还可以查看到前后版本之前的差别（系统会使用不同的颜色来进行差别的标识）。该查看方式对所有用户开放。在项目进行中，企业用户可以在该页面对每个版本进行日常的评分和评价。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">企业用户的后台管理</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&mdash;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">项目管理</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&mdash;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">创意</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&mdash;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">查看，进入新页面后，点击标题后，就可以对版本进行查看、对比和评分等操作。右侧的工具栏中，可以在不同的版本之间进行切换，并进行评分和评价。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; </span></span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">企业用户的后台管理</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&mdash;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">项目管理</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&mdash;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">创意</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">&mdash;</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">查看，进入新页面后，点击版本数</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">--</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">查看，在出现的版本列表中，可以随意的勾选版本进行对比，并进行评分和评价。</span></p>\n<p class="MsoNormal" style="text-align: left;"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">如何对比版本及评分</span></strong></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt;">企业用户在每个查看创意的页面中，都可以进行各个版本的对比，以便确定每个版本所有者对该创意的贡献，同时可以对该版本的贡献进行评定（包括评分和评价）。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt;">评分为</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">1-10</span><span style="font-size: 9.0pt;">分，分数越高表示评分者对该版本的创作内容评价越高；不给予分数，表示评分者认为该版本的创作内容对创意方案并无明显的贡献。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt;">所有的用户都可以在项目页的版本页面中查看企业用户给出的分数和评语，也可以查看参与者的版本对比，即企业和智塔网对参与者的评价与评分都处于公开透明的状态，接受所有用户的监督。</span></p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">如何选择中选的创意</span></strong></p>\n<p style="margin-top: 0cm; margin-right: 0cm; margin-bottom: .0001pt; margin-left: 42.55pt; text-align: justify; text-justify: inter-ideograph; text-indent: -11.05pt; mso-list: l5 level1 lfo7;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 9.0pt;">在项目结束并进入投票期之前的一天的打分期中，企业用户可以在项目的若干个创意中选择一个作为中选的方案。</span></p>\n<p style="margin-top: 0cm; margin-right: 0cm; margin-bottom: .0001pt; margin-left: 42.55pt; text-align: justify; text-justify: inter-ideograph; text-indent: -11.05pt; mso-list: l5 level1 lfo7;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 9.0pt;">每个查看创意的页面中，都可以选定中选创意；在打分期中，可以取消选定的创意并进行重新选择</span></p>\n<p style="margin-top: 0cm; margin-right: 0cm; margin-bottom: .0001pt; margin-left: 42.55pt; text-align: justify; text-justify: inter-ideograph; text-indent: -11.05pt; mso-list: l5 level1 lfo7;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 9.0pt;">目前每个项目只能选定一个中选创意。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-font-kerning: 1.0pt;">4. &nbsp;&nbsp;</span><span style="font-size: 9.0pt;">如果企业用户没有正式的选择一个创意作为最终方案，那么系统会按照之前日常中企业和智塔对创意版本的所打的分数，自动选择版本总分数最高的那个方案作为最终方案。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;">&nbsp;</p>\n<p class="MsoNormal"><strong><span style="font-size: 12pt; font-family: 华文行楷; font-weight: normal;">悬赏积分如何分配</span></strong></p>\n<p style="margin: 0cm; margin-bottom: .0001pt;"><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style="font-size: 9pt;">智塔网设计了全新的悬赏分配方式，尽量做到公平公正和透明，并让所有用户都可以亲身参与到悬赏的分配中来。</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">1.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt;">发布方：在创意进行阶段和打分期，为参与者的版本打分数（</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">1-10</span><span style="font-size: 9.0pt;">），分数越高代表对参与者的创意版本越满意；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">2.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt;">发布方：在创意结束后的打分期中（结束后的</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">1</span><span style="font-size: 9.0pt;">天，在打分期中，创意是关闭的，无法继续参与），在若干创意方案中选定一个中选方案；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">3.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp; </span></span><span style="font-size: 9.0pt;">智塔网：在创意进行阶段和打分期，为参与者的版本打分数（</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">1-10</span><span style="font-size: 9.0pt;">）；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><span lang="EN-US" style="text-indent: -11.05pt; font-size: 9pt; font-family: Calibri, sans-serif;">4.&nbsp;</span><span style="text-indent: -11.05pt; font-size: 9pt;">用户参与的创意方案被发布方选中，同时在这个方案中用户发布的版本的分数</span><span lang="EN-US" style="text-indent: -11.05pt; font-size: 9pt; font-family: Calibri, sans-serif;">&gt;0</span><span style="text-indent: -11.05pt; font-size: 9pt;">（即发布方或智塔给出的分数</span><span lang="EN-US" style="text-indent: -11.05pt; font-size: 9pt; font-family: Calibri, sans-serif;">&gt;0</span><span style="text-indent: -11.05pt; font-size: 9pt;">），则用户就会作为投票的候选人出现在投票列表中，接受注册用户的投票；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">5.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9.0pt;">方案在打分期后，进入到</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">3</span><span style="font-size: 9.0pt;">天的投票期。在投票期中，所有的注册用户都可以为心仪的候选人投上自己的票数（不多于</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">3</span><span style="font-size: 9.0pt;">票），来表示自己对候选人的支持；</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><!--[if !supportLists]--><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri;">6.<span style="font-size: 7pt; font-family: ''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size: 9pt;">投票结束后，进行最后的悬赏分配，按照</span><span style="font-size: 9pt;">注册用户投票</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;">/</span><span style="font-size: 9pt;">发布方打分</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;">/</span><span style="font-size: 9pt;">智塔打分</span><span style="font-size: 9pt;">比例为</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;"> 2</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">：</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">2</span><span style="font-size: 9.0pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">：</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">1</span><span style="font-size: 9pt;">来计算最后的悬赏积分。例如：候选人</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;">A</span><span style="font-size: 9pt;">在总数</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">100</span><span style="font-size: 9pt;">票的注册用户投票中获得了</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">30</span><span style="font-size: 9pt;">票；在企业的中选创意打分中，获得了</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">20</span><span style="font-size: 9pt;">分（企业在该创意中总共给出了</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">50</span><span style="font-size: 9pt;">分至数名候选人）；在智塔的该中选创意的打分中，获得了</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">10</span><span style="font-size: 9pt;">分（智塔在该创意中总共给出了</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">50</span><span style="font-size: 9pt;">分至数名候选人）。而该创意悬赏方案的悬赏金额为</span><span lang="EN-US" style="font-size: 9.0pt; font-family: ''Calibri'',''sans-serif''; mso-bidi-font-family: ''Times New Roman''; mso-font-kerning: 1.0pt;">5000</span><span style="font-size: 9pt;">元，则</span><span lang="EN-US" style="font-size: 9pt; font-family: Verdana, sans-serif;">A</span><span style="font-size: 9pt;">获得的悬赏积分计算如下：</span></p>\n<p style="margin: 0cm 0cm 0.0001pt 42.55pt; text-indent: -11.05pt; text-align: left;"><span style="font-size: 9pt;">[(30/100)X2+(20/50)X2+(10/50)X1]/(2+2+1)X5000=1600 元</span></p>', 6, 4, 1392277122, 1, 0, 6);


